INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (19886, 'Glucagen 1mg/ml vial', 'جلوكاجين 1مجم/مل فيال', '323', '230', 'Anti hypoglycemic', 'Glucagon', 'Novo nordisk > egyptian pharmaceutical trading company', 'Vial', 'فيال', '1mg', '1', NULL, 'about glucagon pancreatic hormone anti hypoglycemic antidote in insulin coma diagnostic agent. mechanism of action of glucagon glucagon exerts it`s therapeutic action by acting through its own receptor and coupling gs protein activates adenylyl cyclase an', NULL, NULL, 2170, '2024-11-17', 'Glucagon is indicated as a diagnostic aid in radiologic exams to temporarily inhibit the movement of the gastrointestinal tract and to treat severe hypoglycemia.[L7634,L7637,L7640,L7643,L8519]', 'Glucagon binds to the glucagon receptor activating G<sub>s</sub>α and G<sub>q</sub>.[A19402] This activation activates adenylate cyclase, which increases intracellular cyclic AMP and activates protein kinase A.[A19402] Activating G<sub>q</sub> activates phospholipase C, increases production of inositol 1,4,5-triphosphate, and releases intracellular calcium.[A19402] Protein kinase A phosphorylates glycogen phosphorylase kinase, which phosphorylates glycogen phosphorylase, which phosphorylates glycogen, causing its breakdown.[A19402]



Glucagon also relaxes smooth muscle of the stomach, duodenum, small bowel, and colon.[L7634,L7643]', 'Glucagon is indicated as a diagnostic aid in radiologic exams to temporarily inhibit the movement of the gastrointestinal tract and severe hypoglycemia.[A19402,L7634,L7637,L7640,L7643,L8519] Glucagon raises blood sugar through activation of hepatic glucagon receptors, stimulating glycogenolysis and the release of glucose.[L7640,L7643] Glucagon has a short duration of action.[L7634,L7637,L7640,L7643] Glucagon may cause hyperglycemia in diabetic patients.[L7634,L7637,L7640,L7643]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (4881, 'Advancevela max massage 50gm cream', 'ادفانسفيلا ماكس مساج كريم 50 جم', '65', '50', 'Massage', 'Salicylic acid+mentha piperita oil+eucalyptus oil+curcuma longa root oil+menthol+eugenia caryophyllus leaf oil+camellia sinensis extract+tocopherol+triethanolamine+disodium edta+stearic acid+phenoxyethanol+emu oil+aqua+pantheno', 'Advance pharma', 'Cream', 'كريم', '50gm', '1', NULL, NULL, NULL, NULL, 1027, '2025-01-30', 'Key additive in many skin-care products for the treatment of acne, psoriasis, callouses, corns, keratosis pilaris and warts.', 'Salicylic acid directly irreversibly inhibits COX-1 and COX-2 to decrease conversion of arachidonic acid to precursors of prostaglandins and thromboxanes. Salicylate''s use in rheumatic diseases is due to it''s analgesic and anti-inflammatory activity. Salicylic acid is a key ingredient in many skin-care products for the treatment of acne, psoriasis, calluses, corns, keratosis pilaris, and warts. Salicylic acid allows cells of the epidermis to more readily slough off. Because of its effect on skin cells, salicylic acid is used in several shampoos used to treat dandruff. Salicylic acid is also used as an active ingredient in gels which remove verrucas (plantar warts). Salicylic acid competitively inhibits oxidation of uridine-5-diphosphoglucose (UDPG) with nicotinamide adenosine dinucleotide (NAD) and noncompetitively with UDPG. It also competitively inhibits the transferring of the glucuronyl group of uridine-5-phosphoglucuronic acid (UDPGA) to a phenolic acceptor. Inhibition of mucopoly saccharide synthesis is likely responsible for the slowing of wound healing with salicylates.', 'Salicylic acid treats acne by causing skin cells to slough off more readily, preventing pores from clogging up. This effect on skin cells also makes salicylic acid an active ingredient in several shampoos meant to treat dandruff. Use of straight salicylic solution may cause hyperpigmentation on unpretreated skin for those with darker skin types (Fitzpatrick phototypes IV, V, VI), as well as with the lack of use of a broad spectrum sunblock. Subsalicylate in combination with bismuth form the popular stomach relief aid known commonly as Pepto-Bismol. When combined the two key ingredients help control diarrhea, nausea, heartburn, and even gas. It is also very mildly anti-biotic.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (11895, 'Unicetamol 100mg/ml oral drops 15 ml', 'يونيسيتامول 15مل نقط للاطفال', '23', '15', 'Antipyretic', 'Paracetamol(acetaminophen)', 'Unipharma co.', 'Drops', 'نقط', '100mg', '1', 'خافض للحرارة', 'about paracetamol acetanilide derivative non narcotic analgesic antipyretic. mechanism of action of paracetamol paracetamol has analgesic and antipyretic action. it is more active on cyclo-oxygenase enzyme in brain. peripherally it is a poor inhibitor of', '6224001183959', NULL, 3796, '2024-07-12', NULL, NULL, NULL, NULL, 0, 0, 0);
