INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Velbastine 10mg vial for i.v. inf.', 29073, 'فيلباستين 10مجم فيال للحقن الوريدي', '60', NULL, 'Antineoplastic', 'Antineoplastic', NULL, 'Vinblastine sulphate', 'Korea united pharma > eimc', 'Vial', 'فيال', '10mg', '1', NULL, NULL, NULL, NULL, 696, '2022-07-11', NULL, 0, 'For treatment of breast cancer, testicular cancer, lymphomas, neuroblastoma, Hodgkin''s and non-Hodgkin''s lymphomas, mycosis fungoides, histiocytosis, and Kaposi''s sarcoma.', 'The antitumor activity of vinblastine is thought to be due primarily to inhibition of mitosis at metaphase through its interaction with tubulin. Vinblastine binds to the microtubular proteins of the mitotic spindle, leading to crystallization of the microtubule and mitotic arrest or cell death.', 'Vinblastine is a vinca alkaloid antineoplastic agent. The vinca alkaloids are structurally similar compounds comprised of 2 multiringed units: vindoline and catharanthine. The vinca alkaloids have become clinically useful since the discovery of their antitumour properties in 1959. Initially, extracts of the periwinkle plant (<i>Catharanthus roseus</i>) were investigated because of putative hypoglycemic properties, but were noted to cause marrow suppression in rats and antileukemic effects <i>in vitro</i>. Vinblastine has some immunosuppressant effect. The vinca alkaloids are considered to be cell cycle phase-specific.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Vincristine sulphate 1mg/ml (1mg) i.v. vial', 12310, 'فينكريستين سالفات 1مجم/مل فيال', '19', NULL, 'Antineoplastic', 'Antineoplastic', NULL, 'Vincristine', 'Hospira inc.-uk > pfizer scientific office', 'Vial', 'فيال', '1mg', '1', NULL, NULL, 'about vincristine natural (vinca) alkaloid a mitotic inhibitor antineoplastic. mechanism of action of vincristine vincristine binds to the tubuline dimers (tubuline is a structural protein which polymerizes to form microtubules) and inhibit the polymeriza', NULL, 1165, '2023-06-03', NULL, 0, 'Treatment of acute lymphocytic leukemia (ALL), Hodgkin lymphoma, non-Hodgkin lymphomas, Wilms'' tumor, neuroblastoma, rhabdomyosarcoma. Liposomal vincristine is indicated for the treatment of relapsed Philadelphia chromosome-negative (Ph-) acute lymphoblastic leukemia (ALL). ', 'The antitumor activity of Vincristine is thought to be due primarily to inhibition of mitosis at metaphase through its interaction with tubulin. Like other vinca alkaloids, Vincristine may also interfere with: 1) amino acid, cyclic AMP, and glutathione metabolism, 2) calmodulin-dependent Ca<sup>2+</sup>-transport ATPase activity, 3) cellular respiration, and 4) nucleic acid and lipid biosynthesis.', 'Vincristine is a vinca alkaloid antineoplastic agent used as a treatment for various cancers including breast cancer, Hodgkin''s disease, Kaposi''s sarcoma, and testicular cancer. The vinca alkaloids are structurally similar compounds comprised of 2 multiringed units, vindoline and catharanthine. The vinca alkaloids have become clinically useful since the discovery of their antitumour properties in 1959. Initially, extracts of the periwinkle plant (Catharanthus roseus) were investigated because of putative hypoglycemic properties, but were noted to cause marrow suppression in rats and antileukemic effects <i>in vitro</i>. Vincristine binds to the microtubular proteins of the mitotic spindle, leading to crystallization of the microtubule and mitotic arrest or cell death. Vincristine has some immunosuppressant effect. The vinca alkaloids are considered to be cell cycle phase-specific.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Vitamin c 500mg 20 caps.', 29497, 'فيتامين سي 500مجم 20 كبسولة', '50', NULL, 'Vitamin c', 'Vitamin c', NULL, 'Vitamin c', 'Epci', 'Cap', 'كبسولة', '500mg', '2', NULL, NULL, 'about vitamin c water soluble vitamin antioxidant. mechanism of action of vitamin c vitamin c exerts it`s action by influencing the biologic oxidations and reductions used in cellular respirations. it directly stimulates collagen synthesis and maintains i', NULL, 1314, '2022-07-07', NULL, 0, 'For the treatment of vitamin A deficiency.', 'Vision:Vitamin A (all-<i>trans</i> retinol) is converted in the retina to the 11-<i>cis</i>-isomer of retinaldehyde or 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal functions in the retina in the transduction of light into the neural signals necessary for vision. 11-<i>cis</i>-retinal, while attached to opsin in rhodopsin is isomerized to all-<i>trans</i>-retinal by light. This is the event that triggers the nerve impulse to the brain which allows for the perception of light. All-<i>trans</i>-retinal is then released from opsin and reduced to all-<i>trans</i>-retinol. All-<i>trans</i>-retinol is isomerized to 11-<i>cis</i>-retinol in the dark, and then oxidized to 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal recombines with opsin to re-form rhodopsin. Night blindness or defective vision at low illumination results from a failure to re-synthesize 11-<i>cis</i> retinal rapidly. Epithelial differentiation: The role of Vitamin A in epithelial differentiation, as well as in other physiological processes, involves the binding of Vitamin A to two families of nuclear retinoid receptors (retinoic acid receptors, RARs; and retinoid-X receptors, RXRs). These receptors function as ligand-activated transcription factors that modulate gene transcription. When there is not enough Vitamin A to bind these receptors, natural cell differentiation and growth are interrupted.', 'Vitamin A is effective for the treatment of Vitamin A deficiency. Vitamin A refers to a group of fat-soluble substances that are structurally related to and possess the biological activity of the parent substance of the group called all-<i>trans</i> retinol or retinol. Vitamin A plays vital roles in vision, epithelial differentiation, growth, reproduction, pattern formation during embryogenesis, bone development, hematopoiesis and brain development. It is also important for the maintenance of the proper functioning of the immune system.', 'DrugBank');
