INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Mira-isoflurane 100% volatile liquid for inhalation 100ml', 22828, 'ميرا ايزوفلوران 100% فولاتايل للاستنشاق 100مل', '110', NULL, 'General anaesthesia', 'General anaesthesia', NULL, 'Isoflurane', 'Arab caps > mira international', 'Unknown', 'غير محدد', '100%', '1', NULL, NULL, 'description a stable non-explosive inhalation anesthetic relatively free from significant side effects. mechanism of action isoflurane induces a reduction in junctional conductance by decreasing gap junction channel opening times and increasing gap juncti', NULL, 994, '2022-07-08', NULL, 0, 'For induction and maintenance of general anesthesia.', 'Isoflurane induces a reduction in junctional conductance by decreasing gap junction channel opening times and increasing gap junction channel closing times. Isoflurane also activates calcium dependent ATPase in the sarcoplasmic reticulum by increasing the fluidity of the lipid membrane. Also appears to bind the D subunit of ATP synthase and NADH dehydogenase. Isoflurane also binds to the GABA receptor, the large conductance Ca<sup>2+</sup> activated potassium channel, the glutamate receptor and the glycine receptor.', 'Isoflurane is a general inhalation anesthetic used for induction and maintenance of general anesthesia. It induces muscle relaxation and reduces pains sensitivity by altering tissue excitability. It does so by decreasing the extent of gap junction mediated cell-cell coupling and altering the activity of the channels that underlie the action potential.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('G-regulator 24mg/5ml 125ml susp.', 20071, 'جي-ريجليتور 24 مجم / 5 مل معلق 125 مل', '7', NULL, 'Antimuscarinic.irritable bowel syndrome', 'Antimuscarinic.irritable bowel syndrome', NULL, 'Trimebutine', 'T3a pharma', 'Susp', 'معلق', '24mg', '1', '1- علاج حالات ارتجاع المريء والمعدة. 2- القيء والغثيان والترجيع وعدم قبول الطعام. 3- تحسين حركية الأمعاء لتصريف الطعام. 4- الشعور بتوقف الطعام في الصدر. 5- القولون العصبي واضطراب الجهاز الهضمي. 6- حالات الإمساك المزمن وآلام البطن. 7- حالات المغص و الانتفاخ والتقلصات.', NULL, NULL, '6221014000136', 818, '2023-05-04', NULL, 0, 'Indicated for symptomatic treatment of irritable bowel syndrome (IBS) and treatment of postoperative paralytic ileus following abdominal surgery. ', 'At high concentrations, trimebutine is shown to inhibit the extracellular Ca2+ influx in the smooth muscle cells through voltage dependent L-type Ca2+ channels and further Ca2+ release from intracellular Ca2+ stores [A19691, A19689]. Trimebutine is suggested to bind to the inactivated state of the calcium channel with high affinity. Reduced calcium influx attenuates membrane depolarization and decrease colon peristalsis. It also inhibits outward K+ currents in response to membrane depolarization of the GI smooth muscle cells at resting conditions through inhibition of delayed rectifier K+ channels and Ca2+ dependent K+ channels, which results in induced muscle contractions [A19691, A19695]. Trimebutine binds to mu opioid receptors with more selectivity compared to delta or kappa opioid receptors but with lower affinity than their natural ligands. Its metabolites (N-monodesmethyl-trimebutine or nor-trimebutine), are also shown to bind to opoid receptors on brain membranes and myenteric synaptosomes [A19692]. ', 'Trimebutine is a spasmolytic agent that acts directly on smooth muscle to modulate gastric motility. It shows a "dual function" that stimulates or inhibits spontaneous contractions depending on the concentration and prior contractile activity in the preparation. Targeting ion conductance that regulates GI motility, trimebutine inhibits the inward calcium currents and calcium-dependent potassium currents in a concentration-dependent manner [A19691]. At lower concentrations (1-10uM), trimebutine depolarizes the resting membrane potential without affecting the amplitude of contractions, which is thought to be mediated by inhibition of outward potassium currents. It is also shown to activate T-type Ca2+ channel and increase gastric emptying, intestinal and colonic contractility [A19690]. At higher concentrations (100-300uM), reduced amplitude of spontaneous contractions and action potentials is thought to be mediated by inhibition of L-type Ca2+ channels and inward calcium current [A19691]. Trimebutine mediates a local anesthetic action by acting as a weak agonist at mu opioid receptors.', 'DrugBank');
