INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Santrone 20mg/10ml vial for i.v. inf.', 26545, 'سانترون 20مجم/10 مل فيال', '2,500', '1700', 'Antineoplastic', 'Antineoplastic', NULL, 'Mitoxantrone', 'Hikma specialized pharmaceuticals', 'Vial', 'فيال', '20mg', '1', NULL, NULL, NULL, NULL, 1098, '2024-12-05', NULL, 0, 'For the treatment of secondary (chronic) progressive, progressive relapsing, or worsening relapsing-remitting multiple sclerosis', 'Mitoxantrone, a DNA-reactive agent that intercalates into deoxyribonucleic acid (DNA) through hydrogen bonding, causes crosslinks and strand breaks. Mitoxantrone also interferes with ribonucleic acid (RNA) and is a potent inhibitor of topoisomerase II, an enzyme responsible for uncoiling and repairing damaged DNA. It has a cytocidal effect on both proliferating and nonproliferating cultured human cells, suggesting lack of cell cycle phase specificity.', 'Mitoxantrone has been shown in vitro to inhibit B cell, T cell, and macrophage proliferation and impair antigen presentation, as well as the secretion of interferon gamma, TNFa, and IL-2.', 'DrugBank');
