INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Flixonase 50 mcg/metered nasal spray 120 dose', 19137, 'فليكسونيز مائي بخاخ للانف 50مكجم/جرعة سبراي', '133', '90', 'Anti-inflammatory.glucocorticoid', 'Anti-inflammatory.glucocorticoid', NULL, 'Fluticasone propionate', 'Glaxo smithkline', 'Spray', 'بخاخ', '50 mcg', '1', 'مضاد للالتهابات يستخدم لعلاج اعراض البرد والحساسية بما في ذلك انسداد الأنف وسيلانه أو حكة في الأنف والعطس ودموع العين وحكة أو احمرار العينين', NULL, NULL, '5054290005738', 11450, '2025-02-26', NULL, 0, 'Fluticasone propionate is indicated as an inhaler for the treatment and management of asthma by prophylaxis[FDA Label]as well as inflammatory and pruritic dermatoses[F4355]. Fluticasone propionate nasal spray is indicated for managing allergic and nonallergic rhinitis[L8309,F4358].', 'Fluticasone propionate works through an unknown mechanism to  affect the action of various cell types and mediators of inflammation[F4358]. Fluticasone propionate activates glucocorticoid receptors and inhibits lung eosinophilia in rats[F4358,F4355].', 'Systemically, fluticasone propionate activates glucocorticoid receptors, and inhibits lung eosinophilia in rats[F4358][FDA Label]. Fluticasone propionate as a topical formulation is also associated with vasoconstriction in the skin[F4355,A177118].', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Eletrikan 40 mg 3 f.c. tab.', 18155, 'اليتركان 40 مجم 3 اقراص', '48', NULL, 'Antimigraine', 'Antimigraine', NULL, 'Eletriptan', 'Hikma pharma', 'Tab', 'أقراص', '40 mg', '1', 'علاج الصداع النصفى', NULL, 'about eletriptan selective 5 ht 1b/1d agonist antimigraine agent mechanism of action of eletriptan eletriptan is a selective 5-hydroxytryptamine (serotonin5-ht) type 1b and 1d (5-ht1b/1d) receptor agonist. it is thought to be related to activation of 5-ht', NULL, 1118, '2022-07-08', NULL, 0, 'For the acute treatment of migraine with or without aura in adults.', 'Eletriptan binds with high affinity to 5-HT1B, 5-HT1D and 5-HT1F receptors, has modest affinity for 5-HT1A, 5-HT1E, 5-HT2B and 5-HT7 receptors, and little or no affinity for 5-HT2A, 5-HT2C, 5-HT3, 5-HT4, 5-HT5A and 5-HT6 receptors. In contrast, eletriptan displays insignificant pharmacological activity at adrenergic alpha1, alpha2, or beta; dopaminergic D1 or D2; muscarinic; or opioid receptors. While the full mechanism of action of 5-HT receptor agonists in relieving migrains is not fully elucidated, it is proposed that the activation of 5-HT1 receptors located on intracranial blood vessels leads to vasoconstriction that correlates with the relief of migraine headaches. It is also proposed that the activation of 5-HT1 receptors on sensory nerve endings in the trigeminal system leads to the inhibition of release of pro-inflammatory neuropeptides. ', 'Eletriptan is a selective 5-hydroxytryptamine 1B/1D receptor agonist. In the anesthetized dog, eletriptan has been shown to reduce carotid arterial blood flow, with only a small increase in arterial blood pressure at high doses. While the effect on blood flow was selective for the carotid arterial bed, decreases in coronary artery diameter were observed. Eletriptan has also been shown to inhibit trigeminal nerve activity in the rat.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Harvy cream 60 gm', 5941, 'هارفي كريم 60 جم', '125', '110', 'Whitening topical', 'Whitening topical', NULL, 'Kojic acid+licuorice extract+royal jelly+vitamin c+glycolic acid+allantoin+zinc oxide+titanium dioxi', 'Hi-care > roventis pharma', 'Cream', 'كريم', '60 gm', '1', NULL, NULL, '*** indications ï- melasma. ï- freckles. ï- age spots. ï- post ï- inflammatory hyperpigmentaion (due to acne prolonged sun exposure or after cosmetic procedures such as laser peeling ï- etc). ï- dark patches in the body. ***directions for use apply it onc', '6224011042116', 4248, '2025-01-02', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Globvitron 30 capsule', 9329, 'جلوبفيترون 30 كبسولة', '117', '99', 'Multivitamin', 'Multivitamin', NULL, 'Ferrous bisglycinate35mg+vitamin b12 1000mcg+vit c+vit b1+vit b3+vit b2+vit b6+folate 600mcg+copper', 'Medicina', 'Capsule', 'كبسولة', NULL, '3', NULL, NULL, NULL, NULL, 925, '2024-07-14', NULL, 0, 'Ferrous sulfate is used for the prevention and treatment of iron deficiency anemia in adults and children.[A190804,L2240,L11800]

', 'Iron is required to maintain optimal health, particularly for helping to form red blood cells (RBC) that carry oxygen around the body. A deficiency in iron indicates that the body cannot produce enough normal red blood cells.[A32514,L11800] Iron deficiency anemia occurs when body stores of iron decrease to very low levels, and the stored iron is insufficient to support normal red blood cell (RBC) production. Insufficient dietary iron, impaired iron absorption, bleeding, pregnancy, or loss of iron through the urine can lead to iron deficiency.[A32514,L11794]  Symptoms of iron deficiency anemia include fatigue, breathlessness, palpitations, dizziness, and headache.



Taking iron in supplement form, such as ferrous sulfate, allows for more rapid increases in iron levels when dietary supply and stores are not sufficient.[L2175] Iron is transported by the divalent metal transporter 1 (DMT1) across the endolysosomal membrane to enter the macrophage. It can then can be incorporated into ferritin and be stored in the macrophage or carried of the macrophage by ferroportin. This exported iron is  oxidized by the enzyme to ceruloplasmin to Fe3+, followed by sequestration by transferrin for transport in the serum to various sites, including the bone marrow for hemoglobin synthesis or into the liver.[A32524] Iron combines with porphyrin and globin chains to form hemoglobin, which is critical for oxygen delivery from the lungs to other tissues.[L2263]', 'Ferrous sulfate replenishes iron, an essential component in hemoglobin, myoglobin, and various enzymes. It replaces the iron that is usually found in hemoglobin and myoglobin. Iron participates in oxygen transport and storage, electron transport and energy metabolism, antioxidant and beneficial pro-oxidant functions, oxygen sensing, tissue proliferation and growth, as well as DNA replication and repair.[A32524,A32514]



', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Loxapine 25mg 10caps.', 22082, 'لوكسابين 25مجم 10 كبسولات', '8', NULL, 'Psychiatric.antipsychotics', 'Psychiatric.antipsychotics', NULL, 'Loxapine', 'Global napi pharmaceuticals > al delta pharmaceuticals trading co.', 'Cap', 'كبسولة', '25mg', '1', NULL, NULL, 'about loxapine a dibenzazepine derivative a typical antipsychotic. mechanism of action of loxapine loxapine produce its antipsychotic activity by blocking the post synaptic receptor in the cns. indications for loxapine psychoses interactions for loxapine', NULL, 554, '2022-08-17', NULL, 0, 'For the management of the manifestations of psychotic disorders such as schizophrenia', 'Loxapine is a dopamine antagonist, and also a serotonin 5-HT2 blocker. The exact mode of action of Loxapine has not been established, however changes in the level of excitability of subcortical inhibitory areas have been observed in several animal species in association with such manifestations of tranquilization as calming effects and suppression of aggressive behavior.', 'Loxapine, a dibenzoxazepine compound, represents a subclass of tricyclic antipsychotic agents, chemically distinct from the thioxanthenes, butyrophenones, and phenothiazines. Pharmacologically, Loxapine is a tranquilizer for which the exact mode of action has not been established, however, it is believed that by antagonising dopamine and serotonin receptors, there is a marked cortical inhibition which can manifest as tranquilization and suppression of aggression.', 'DrugBank');
