INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (22489, 'Meki ah 2.5mg/5ml syrup 60 ml', 'ميكي ايه اتش 2.5مجم/5مل شراب 60 مل', '23', '14', 'Antihistamine.antiallergy', 'Mequitazine', 'Borg', 'Syrup', 'شراب', '2.5mg', '1', NULL, 'about mequitazine h 1 antagonist sedative phenothiazine derivative mechanism of action of mequitazine mequitazine binds to the histamine h1 receptor sites on effector cells in the gastrointestinal tract blood vessels and respiratory tract. this blocks the', '6223002570003', NULL, 902, '2025-09-25', 0, 'For the treatment of Hay fever, urticaria (hives) and allergic rhinitis', 'Mequitazine binds to the histamine H1 receptor sites on effector cells in the gastrointestinal tract, blood vessels, and respiratory tract. This blocks the action of endogenous histamine, which subsequently leads to temporary relief of the negative symptoms brought on by histamine.', 'In allergic reactions an allergen interacts with and cross-links surface IgE antibodies on mast cells and basophils. Once the mast cell-antibody-antigen complex is formed, a complex series of events occurs that eventually leads to cell-degranulation and the release of histamine (and other chemical mediators) from the mast cell or basophil. Once released, histamine can react with local or widespread tissues through histamine receptors. Histamine, acting on H<sub>1</sub>-receptors, produces pruritis, vasodilatation, hypotension, flushing, headache, tachycardia, and bronchoconstriction. Histamine also increases vascular permeability and potentiates pain. Mequitazine is a histamine H1 antagonist. It competes with histamine for the normal H<sub>1</sub>-receptor sites on effector cells of the gastrointestinal tract, blood vessels and respiratory tract. It provides effective, temporary relief of sneezing, watery and itchy eyes, and runny nose due to hay fever and other upper respiratory allergies.', 'DrugBank', 0, 0, 0);
