INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (22888, 'Modulol 5 mg 14 tab.', 'موديولول 5مجم 14 قرص', '20', NULL, 'Antihypertensive.beta blocker.selective b1', 'Nebivolol hydrochloride', 'Pharopharma', 'Tab', 'أقراص', '5 mg', '2', NULL, 'about nebivololsthird generation cardio selective beta-1 receptor antagonist antihypertensive.smechanism of action of nebivololsnebivolol is a 3rd generation cardio selective beta-1 receptor antagonist which reduces vascular resistance acutely lowers arte', NULL, NULL, 1123, '2022-06-18', 0, 'Nebivolol is indicated to treat hypertension.[A2762,A182579,L7985,L7988]', 'Nebivolol is a highly selective beta-1 adrenergic receptor antagonist[A182579] with weak beta-2 adrenergic receptor antagonist activity.[A182585] Blocking beta-1 adrenergic receptors by d-nebivolol leads to decreased resting heart rate, exercise heart rate, myocardial contracility, systolic blood pressure, and diastolic blood pressure.[A182603,A182579,A182585,A182615] The selectivity of d-nebivolol limits the magnitude of beta blocker adverse effects in the airways or relating to insulin sensitivity.[A182615] Nebivolol also inhibits aldosterone, and beta-1 antagonism in the juxtaglomerular apparatus also inhibits the release of renin.[A182615] Decreased aldosterone leads to decreased blood volume, and decreased renin leads to reduced vasoconstriction.[A182615] l-nebivolol is responsible for beta-3 adrenergic receptor agonist activity that stimulates endothelial nitric oxide synthase, increasing nitric oxide levels; leading to vasodilation, decreased peripheral vascular resistance, increased stroke volume, ejection fraction, and cardiac output.[A2762,A182603,A182579,A182585,A182615] The vasodilation, reduced oxidative stress, and reduced platelet volume and aggregation of nebivolol may lead to benefits in heart failure patients.[A182603]', 'Nebivolol is a selective beta-1 adrenergic receptor antagonist that decreases vascular resistance, increases stroke volume and cardiac output, and does not negatively affect left ventricular function.[A182579,A182585] It has a long duration of action as effects can be seen 48 hours after stopping the medication and a wide therapeutic window as patients generally take 5-40mg daily.[A182579,L7985] Patients should not abruptly stop taking this medication as this may lead to exacerbation of coronary artery disease.[L7985] Diabetic patients should monitor their blood glucose levels as beta blockers may mask signs of hypoglycemia.[L7985]', 'DrugBank', 0, 0, 0);
