INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (23057, 'Mucalgin susp. 100ml', 'موكالجين معلق 100 مل', '3', NULL, 'Antacid', 'Aluminium hydroxide+magnesium hydroxide+oxethazine', 'Alfacure pharmaceuticals', 'Susp', 'معلق', '100ml', '1', NULL, 'about aluminium hydroxide antacid antidote protectant( topical) in hyperacidity; hyperphosphatemia. mechanism of action of aluminium hydroxide it neutralizes and reduces stomach acid relieving heartburn (acid reflux gerd) and indigestion. this results inc', '6225000331884', NULL, 932, '2022-07-12', 0, NULL, 'Aluminum Acetate is an astringent.  An astrignent is a chemical that tends to shrink or constrict body tissues, usually locally after topical medicinal application.  The shrinkage or constriction is through osmotic flow of water (or other fluids) away from the area where the astringent was applied.  Astringent medicines cause shrinkage of mucous membranes or exposed tissues and are often used internally to check discharge of blood serum or mucous secretions. This can happen with a sore throat, hemorrhages, diarrhea, or with peptic ulcers. Externally applied astringents, which cause mild coagulation of skin proteins, dry, harden, and protect the skin. Acne sufferers are often advised to use astringents if they have oily skin. Astringents also help heal stretch marks and other scars. Mild astringent solutions are used in the relief of such minor skin irritations as those resulting from superficial cuts, allergies, insect bites, or fungal infections such as athlete''s foot. ', NULL, 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (23058, 'Mucasert 2% topical cream 15 gm', 'ميوكازيرت 2% كريم 15جم', '11', NULL, 'Antifungals.imidazoles', 'Sertaconazole', 'El nile.', 'Cream', 'كريم', '2%', '1', NULL, 'indication for the topical treatment of interdigital tinea pedis in immunocompetent patients 12 years of age and older caused by trichophyton rubrum trichophyton mentagrophytes and epidermophyton floccosum. mechanism of action sertaconazole interacts with', '6221035003062', NULL, 993, '2022-07-14', 0, 'For the topical treatment of interdigital tinea pedis in immunocompetent patients 12 years of age and older, caused by <i>Trichophyton rubrum</i>, <i>Trichophyton mentagrophytes</i>, and <i>Epidermophyton floccosum</i>.', 'Sertaconazole interacts with 14-&alpha; demethylase, a cytochrome P-450 enzyme necessary to convert lanosterol to ergosterol. As ergosterol is an essential component of the fungal cell membrane, inhibition of its synthesis results in increased cellular permeability causing leakage of cellular contents. Sertaconazole may also inhibit endogenous respiration, interact with membrane phospholipids, inhibit the transformation of yeasts to mycelial forms, inhibit purine uptake, and impair triglyceride and/or phospholipid biosynthesis.', 'Sertaconazole is an imidazole/triazole type antifungal agent. Sertaconazole is a highly selective inhibitor of fungal cytochrome P-450 sterol C-14 &alpha;-demethylation via the inhibition of the enzyme cytochrome P450 14&alpha;-demethylase. This enzyme converts lanosterol to ergosterol, and is required in fungal cell wall synthesis. The subsequent loss of normal sterols correlates with the accumulation of 14 &alpha;-methyl sterols in fungi and may be partly responsible for the fungistatic activity of fluconazole. Mammalian cell demethylation is much less sensitive to fluconazole inhibition. Sertaconazole exhibits <i>in vitro</i> activity against <i>Cryptococcus neoformans</i> and <i>Candida spp.</i> Fungistatic activity has also been demonstrated in normal and immunocompromised animal models for systemic and intracranial fungal infections due to <i>Cryptococcus neoformans</i> and for systemic infections due to Candida albicans.', 'DrugBank', 0, 0, 0);
