INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (20359, 'Adapalene duo forte gel 30 gm', 'ادابالين ديو فورت جل 30 جرام', '79', NULL, 'Anti-acne', 'Adapalene', 'Borg', 'Gel', 'جل', '30 gm', '1', NULL, NULL, NULL, NULL, 1264, '2024-07-15', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Acne vulgaris is a multifactorial disorder of the pilosebaceous unit involving increased sebum production, inflammation, and hyperproliferation/hyperkeratinization of the follicular infundibulum. It is also associated with _Cutibacterium acnes_ (also known as _Propionibacterium acnes_). Adapalene is a third-generation topical retinoid used for the treatment of acne vulgaris.[A193518] Adapalene has similar efficacy but a superior safety profile compared to tretinoin.[A193521] [Tazarotene] is more efficacious than adapalene but is designated as pregnancy category X and hence is contraindicated in pregnant women.[A193518] Adapalene can also be combined with benzoyl peroxide (BPO), which possesses bactericidal properties[A193524], and either adapalene alone, or adapalene BPO combination products, are commonly used to treat mild-to-severe acne.[A193518]

Differin®, produced by Galderma Labs, was first granted FDA approval on May 31st, 1996, as a 0.1% adapalene topical solution. Differin was later made available as 0.1% gel, cream, or lotion, or 0.3% gel products. On December 8th, 2008, Galderma Labs gained FDA approval for Epiduo®, a 0.1% adapalene, 2.5% BPO combination gel.[L12873]', '["D10AD03", "D10AD53"]', '[]');
