INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (20574, 'Hyalgan 20mg/2ml intra-articular prefilled syringe', 'هيالجان 20 مجم / 2 مل حقنة مملوءة مسبقا', '531', '371', 'Joints care', 'Hyaluronic acid', 'Fidia italy > phoenix marketing group', 'Syringe', 'حقنة', '20mg', '1', NULL, NULL, NULL, NULL, 6688, '2025-04-05', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Hyaluronic acid (HA) is an anionic, nonsulfated glycosaminoglycan found in connective, epithelial, and neural tissues; it was first isolated in 1934. Karl Meyer and John Palmer obtained glycosaminoglycan (GAG) from the bovine eye, giving it the name “hyaluronic acid”. HA is involved in many important physiological processes, including but not limited to wound healing, tissue regeneration, and joint lubrication. It demonstrates unique viscoelasticity, moisturizing, anti-inflammatory qualities, and other important properties that prove beneficial in various clinical applications.[A230128]  

