        has_drug_interaction, has_food_interaction, has_disease_interaction
    ) VALUES (9025, 'Dapagliflozin-eva 5 mg 30 f.c. tabs.', 'داباجليفوزين ايفا 5 مجم 30 قرص', '173', NULL, 'Anti-diabetic.secretagogues.sglt2 inhibitors', 'Dapagliflozin', 'Eva pharma', 'Tab', 'أقراص', '5 mg', '3', NULL, 'Dapagliflozin is indicated as an adjunct treatment, alongside diet and exercise, to improve glycemic control in patients ≥10 years of age with type 2 diabetes mellitus.[L51023,A6757,A6758] For patients with chronic kidney disease at risk of progression, dapagliflozin in used to reduce the risk of sustained eGFR decline, end-stage kidney disease, cardiovascular death, and hospitalization for heart failure.[L46372] Dapagliflozin is also indicated to either reduce the risk of cardiovascular death, hospitalization for heart failure, and urgent heart failure visit in adults with heart failure or reduce the risk of hospitalization for heart failure in adults with type 2 diabetes mellitus and either established cardiovascular disease or multiple cardiovascular risk factors.[L46372] Combination products with dapagliflozin also exist, either as a dapagliflozin-saxagliptin or dapagliflozin-metformin hydrochloride formulation.[L38724,L38729] Both are used as an adjunct treatment to diet and exercise to improve glycemic control in adults with type 2 diabetes.[L38724,L38729]', NULL, NULL, 1141, '2022-10-08', 0, 0, 0);
