INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (23149, 'Mycamine 50 mg vial for i.v inf.', 'ميكامين 50مجم فيال للحقن الوريدي', '951', NULL, 'Antifungals.echinocandins', 'Micafungin', 'Astellas toyoma co.ltd-japan > hikma pharma', 'Vial', 'فيال', '50 mg', '1', NULL, 'about micafungin an echinocandin ?-glucan synthase inhibitor antifungal.', NULL, NULL, 599, '2022-08-05', 0, 'Indicated for the treatment of candidemia, acute disseminated candidiasis, and certain other invasive <i>Candida</i> infections, as well as esophageal candidiasis, and prophylaxis of <i>Candida</i> infections in patients undergoing hematopoietic stem cell transplantation. Micafungin is also used as an alternative for the treatment of oropharyngeal candidiases and has been used with some success as primary or salvage therapy, alone or in combination with other antifungals, for the treatment of invasive aspergillosis.



Indicated for the prophylaxis of Candida infections in patients undergoing hematopoietic stem cell transplantation.', 'Micafungin inhibits the synthesis of beta-1,3-D-glucan, an essential component of fungal cell walls which is not present in mammalian cells. It does this by inhibiting beta-1,3-D-glucan synthase.', 'Formerly known as FK463, micafungin is a semisynthetic lipopeptide synthesized from a fermentation product of <i>Coleophoma empetri</i> that works as an antifungal agent. It is a glucan synthesis inhibitor of the echinocandin structural class. The U.S. Food and Drug Administration approved micafungin in March 2005. Micafungin inhibits an enzyme essential for fungal cell-wall synthesis. Depending on its concentration, micafungin may be fungicidal against some <i>Candida</i>, but is usually fungistatic against <i>Apergillus</i>. Micafungin can be used concomitantly with a variety of other drugs, including the HIV protease inhibitor ritonavir and the transplant medications cyclosporine and tacrolimus.', 'DrugBank', 0, 0, 0);
