INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (23264, 'Naratriptan 2.5 mg 2 f.c. tabs.', 'ناراتريبتان 2.5مجم 2 قرص', '45', '38', 'Antimigraine.vasoconstrictor agents.selective serotonin agonist', 'Naratriptan', 'Chanelle pharmaceuticals > diligence pharma', 'Tab', 'أقراص', '2.5 mg', '1', NULL, 'naratriptan is a triptan drug used for the treatment of migraine headaches. naratriptan is available in 2.5 mg tablets. it is a selective 5-ht1 receptor subtype agonist. naratriptan is used for the treatment of the acute migraine attacks and the symptoms', NULL, NULL, 1234, '2025-04-18', 0, 'For the acute treatment of migraine attacks with or without aura in adults.', 'Three distinct pharmacological actions have been implicated in the antimigraine effect of the triptans: (1) stimulation of presynaptic 5-HT1D receptors, which serves to inhibit both dural vasodilation and inflammation; (2) direct inhibition of trigeminal nuclei cell excitability via 5-HT1B/1D receptor agonism in the brainstem and (3) vasoconstriction of meningeal, dural, cerebral or pial vessels as a result of vascular 5-HT1B receptor agonism.', 'Naratriptan is a selective agonist of serotonin (5-hydroxytryptamine; 5-HT) type 1B and 1D receptors. It is structurally and pharmacologically related to other selective 5-HT1B/1D receptor agonist. Naratriptan has only a weak affinity for 5-HT<sub>1A</sub>, 5-HT<sub>5A</sub>, and 5-HT<sub>7</sub> receptors and no significant affinity or pharmacological activity at 5-HT<sub>2</sub>, 5-HT<sub>3</sub> or 5-HT<sub>4</sub> receptor subtypes or at alpha1-, alpha2-, or beta-adrenergic, dopamine1,; dopamine2; muscarinic, or benzodiazepine receptors. This action in humans correlates with the relief of migraine headache. In addition to causing vasoconstriction, experimental data from animal studies show that Naratriptan also activates 5-HT<sub>1</sub> receptors on peripheral terminals of the trigeminal nerve innervating cranial blood vessels, which may also contribute to the antimigrainous effect of Naratriptan in humans.', 'DrugBank', 0, 0, 0);
