INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (23287, 'Natamet 5% eye drops 5 ml (illegal import)', 'ناتاميت 5% قطرة للعين 5مل', '0', NULL, 'Antifungal', 'Natamycin', 'Sun pharmaceutical ind. ltd.', 'Drops', 'نقط', '5%', '1', 'مضاد للفطريات يستخدم فى علاج التهابات العين', 'about natamycin a naturally occurring polyene macrolide antibiotic antifungal antibiotic. side effects of natamycin: conjunctival hyperemia irritation & chemosis. contra-indications of natamycin: hypersensitivity. special precautions while taking natamyci', NULL, NULL, 960, '2022-07-15', 0, 'For the treatment of fungal blepharitis, conjunctivitis, and keratitis caused by susceptible organisms including <i>Fusarium solani</i> keratitis.', 'Like other polyene antibiotics, Natamycin inhibits fungal growth by binding to sterols. Specifically, Natamycin binds to ergosterol in the plasma membrane, preventing ergosterol-dependent fusion of vacuoles, as well as membrane fusion and fission. This differs from the mechanism of most other polyene antibiotics, which tend to work by altering fungal membrane permeability instead.', 'Natamycin is an antifungal drug for topical ophthalmic administration. It is a tetraene polyene antibiotic derived from <i>Streptomyces natalensis</i>. It possesses in vitro activity against a variety of yeast and filamentous fungi, including <i>Candida</i>, <i>Aspergillus</i>, <i>Cephalosporium</i>, <i>Fusarium</i> and <i>Penicillium</i>. Although the activity against fungi is dose-related, natamycin is predominantly fungicidal. Natamycin is not effective in vitro against gram-positive or gram-negative bacteria. Topical administration appears to produce effective concentrations of natamycin within the corneal stroma but not in intraocular fluid.', 'DrugBank', 0, 0, 0);
