INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (23410, 'Nepiralin 200 mg 10 tab.', 'نبيرالين 200مجم 10 اقراص', '90', NULL, 'Antiviral.nucleoside', 'Nevirapine', 'Egpi', 'Tab', 'أقراص', '200 mg', '1', NULL, 'about nevirapine a non-nucleoside reverse transcriptase inhibitor (nnrti) antiretroviral. mechanism of action of nevirapine nevirapine noncompetitively binds to a site other than catalytic site in the hiv- 1 reverse transcriptase enzyme .this binding will', NULL, NULL, 727, '2022-07-16', 0, 'For use in combination with other antiretroviral drugs in the ongoing treatment of HIV-1 infection.', 'Nevirapine binds directly to reverse transcriptase (RT) and blocks the RNA-dependent and DNA-dependent DNA polymerase activities by causing a disruption of the enzyme''s catalytic site. The activity of nevirapine does not compete with template or nucleoside triphosphates.', 'Nevirapine is a non-nucleoside reverse transcriptase inhibitor (nNRTI) with activity against Human Immunodeficiency Virus Type 1 (HIV-1). HIV-2 RT and eukaryotic DNA polymerases (such as human DNA polymerases alpha, beta, or sigma) are not inhibited by nevirapine. Nevirapine is, in general, only prescribed after the immune system has declined and infections have become evident. It is always taken with at least one other HIV medication such as Retrovir or Videx. The virus can develop resistance to nevirapine if the drug is taken alone, although even if used properly, nevirapine is effective for only a limited time.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (23411, 'Nepvol 5 mg 10 tabs. (n/a)', 'نيبفول 5مجم 10 اقراص', '22', NULL, 'Antihypertensive.beta blocker.selective b1', 'Nebivolol hydrochloride', 'El-obour > advocure', 'Tab', 'أقراص', '5 mg', '1', NULL, 'about nebivolol third generation cardio selective beta-1 receptor antagonist antihypertensive. mechanism of action of nebivolol nebivolol is a 3rd generation cardio selective beta-1 receptor antagonist which reduces vascular resistance acutely lowers arte', NULL, NULL, 974, '2022-07-12', 0, 'Nebivolol is indicated to treat hypertension.[A2762,A182579,L7985,L7988]', 'Nebivolol is a highly selective beta-1 adrenergic receptor antagonist[A182579] with weak beta-2 adrenergic receptor antagonist activity.[A182585] Blocking beta-1 adrenergic receptors by d-nebivolol leads to decreased resting heart rate, exercise heart rate, myocardial contracility, systolic blood pressure, and diastolic blood pressure.[A182603,A182579,A182585,A182615] The selectivity of d-nebivolol limits the magnitude of beta blocker adverse effects in the airways or relating to insulin sensitivity.[A182615] Nebivolol also inhibits aldosterone, and beta-1 antagonism in the juxtaglomerular apparatus also inhibits the release of renin.[A182615] Decreased aldosterone leads to decreased blood volume, and decreased renin leads to reduced vasoconstriction.[A182615] l-nebivolol is responsible for beta-3 adrenergic receptor agonist activity that stimulates endothelial nitric oxide synthase, increasing nitric oxide levels; leading to vasodilation, decreased peripheral vascular resistance, increased stroke volume, ejection fraction, and cardiac output.[A2762,A182603,A182579,A182585,A182615] The vasodilation, reduced oxidative stress, and reduced platelet volume and aggregation of nebivolol may lead to benefits in heart failure patients.[A182603]', 'Nebivolol is a selective beta-1 adrenergic receptor antagonist that decreases vascular resistance, increases stroke volume and cardiac output, and does not negatively affect left ventricular function.[A182579,A182585] It has a long duration of action as effects can be seen 48 hours after stopping the medication and a wide therapeutic window as patients generally take 5-40mg daily.[A182579,L7985] Patients should not abruptly stop taking this medication as this may lead to exacerbation of coronary artery disease.[L7985] Diabetic patients should monitor their blood glucose levels as beta blockers may mask signs of hypoglycemia.[L7985]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (23412, 'Nercefaxon 1 gm i.m. vial', 'نيرسيفاكسون 1جم فيال للحقن العضلي', '56', '43', 'Antibiotic.cephalosporin.third-generation', 'Ceftriaxone', 'Badr pharma', 'Vial', 'فيال', '1 gm', '1', NULL, 'about ceftriaxone third generation cephalosporin antibiotic(parenteral). mechanism of action of ceftriaxone ceftriaxone is a 3rd generation cephalosporin which exerts its bactericidal action against both gram positive & gram negative organisms by inhibiti', '6222018600018', NULL, 1248, '2025-05-28', 0, 'Ceftriaxone is used for the treatment of the infections (respiratory, skin, soft tissue, UTI, ENT) caused by susceptible organisms.[L15082] Organisms that are generally susceptible to ceftriaxone include S. pneumoniae, S. pyogenes (group A beta-hemolytic streptococci), coagulase-negative staphylococci, Some Enterobacter spp, H. influenzae, N. gonorrhoeae, P. mirabilis, E. coli, Klebsiella spp, M. catarrhalis, B. burgdorferi, and some oral anaerobes.[A215582] ', 'Ceftriaxone works by inhibiting the mucopeptide synthesis in the bacterial cell wall.[T324,L15082] The beta-lactam moiety of ceftriaxone binds to carboxypeptidases, endopeptidases, and transpeptidases in the bacterial cytoplasmic membrane. These enzymes are involved in cell-wall synthesis and cell division. Binding of ceftriaxone to these enzymes causes the enzyme to lose activity; therefore, the bacteria produce defective cell walls, causing cell death.', 'Ceftriaxone is a cephalosporin/cephamycin beta-lactam antibiotic used in the treatment of bacterial infections caused by susceptible, usually gram-positive, organisms.[L15082] Ceftriaxone has <i>in vitro</i> activity against gram-positive aerobic, gram-negative aerobic, and anaerobic bacteria.[L15087] The bactericidal activity of ceftriaxone results from the inhibition of cell wall synthesis and is mediated through ceftriaxone binding to penicillin-binding proteins (PBPs).[T324] Ceftriaxone is stable against hydrolysis by a variety of beta-lactamases, including penicillinases, and cephalosporinases and extended-spectrum beta-lactamases.[L15082] However, resistance to ceftriaxone usually occurs through beta-lactamase hydrolysis, altered PBPs, or reduced bacterial cell permeability.[T324] Ceftriaxone should not be mixed with or giving in the same IV line as diluents/products containing calcium as they may cause ceftriaxone to precipitate.[L15082] Ceftriaxone use may also cause biliary sludge or gallbladder pseudolithiasis.[L15082,A215602] ', 'DrugBank', 0, 0, 0);
