INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (23715, 'Norhinose 50mcg/dose nasal spray 120 doses', 'نورينوز 50مكجم/جرعة بخاخ للأنف 120 جرعة', '90', '58.5', 'Anti-inflammatory.glucocorticoid.', 'Mometasone furoate', 'European egyptian pharm. ind. > amriya', 'Spray', 'بخاخ', '50mcg', '1', 'مضاد التهابات', 'about mometasone a glucocorticoid steroid anti psoriatic anti asthma anti inflammatory. mechanism of action of mometasone mometasone is a topical medium potency glucocorticoid. the drug exerts its pharmacological action by penetrating and binding to cytop', '6221075160091', NULL, 11745, '2024-07-24', 0, 'The inhaler is indicated for the maintenance treatment of asthma as prophylactic therapy. The nasal spray is indicated for the treatment of the nasal symptoms of seasonal allergic and perennial allergic rhinitis.', 'Unbound corticosteroids cross cell membranes and bind with high affinity to specific cytoplasmic receptors. Inflammation is decreased by diminishing the release of leukocytic acid hydrolases, prevention of macrophage accumulation at inflamed sites, interference with leukocyte adhesion to the capillary wall, reduction of capillary membrane permeability, reduction of complement components, inhibition of histamine and kinin release, and interference with the formation of scar tissue. The antiinflammatory actions of corticosteroids are thought to involve phospholipase A<sub>2</sub> inhibitory proteins, lipocortins, which control the biosynthesis of potent mediators of inflammation such as prostaglandins and leukotrienes. Mometasone furoate has been shown in vitro to exhibit a binding affinity for the human glucocorticoid receptor which is approximately 12 times that of dexamethasone, 7 times that of triamcinolone acetonide, 5 times that of budesonide, and 1.5 times that of fluticasone.', 'Mometasone is a medium-potency synthetic corticosteroid with antiinflammatory, antipruritic, and vasoconstrictive properties. Studies in asthmatic patients have demonstrated that mometasone provides a favorable ratio of topical to systemic activity due to its primary local effect along with the extensive hepatic metabolism and the lack of active metabolites. Though effective for the treatment of asthma, glucocorticoids do not affect asthma symptoms immediately. Maximum improvement in symptoms following inhaled administration of mometasone furoate may not be achieved for 1 to 2 weeks or longer after starting treatment. When glucocorticoids are discontinued, asthma stability may persist for several days or longer. Mometasone has been shown in vitro to exhibit a binding affinity for the human glucocorticoid receptor which is approximately 12 times that of dexamethasone, 7 times that of triamcinolone acetonide, 5 times that of budesonide, and 1.5 times that of fluticasone. The clinical significance of these findings is unknown.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (23719, 'Normal saline 0.9% inj. 500 ml', 'نورمال سالين 0.9% 500 مل', '24', '7.5', 'Normal saline', 'Sodium chloride', 'Haidylena for advanced medical industries > vacsera', 'Inj', 'حقن', '0.9%', '1', NULL, NULL, NULL, NULL, 1749, '2023-08-20', 0, 'This intravenous solution is indicated for use in adults and pediatric patients as a source of electrolytes and water for hydration. Also, designed for use as a diluent and delivery system for intermittent intravenous administration of compatible drug additives. ', 'Sodium and chloride — major electrolytes of the fluid compartment outside of cells (i.e., extracellular) — work together to control extracellular volume and blood pressure. Disturbances in sodium concentrations in the extracellular fluid are associated with disorders of water balance.', 'Sodium, the major cation of the extracellular fluid, functions primarily in the control of water distribution, fluid balance, and osmotic pressure of body fluids. Sodium is also associated with chloride and bicarbonate in the regulation of the acid-base equilibrium of body fluid.

Chloride, the major extracellular anion, closely follows the metabolism of sodium, and changes in the acid-base balance of the body are reflected by changes in the chloride concentration. ', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (23720, 'Normalax 0.75% oral drops 15 ml', 'نورمالاكس 0.75% نقط للفم 15 مل', '12', '3.75', 'Laxative', 'Sodium picosulphate', 'Pharopharma', 'Drops', 'نقط', '0.75%', '1', NULL, 'about sodium picosulphate a locally acting bowel preparation a stimulant laxative. mechanism of action of sodium picosulphate sodium picosulphate is a stimulant laxative. after oral administration it is activated by the bacteria present in the large intes', '6221094010025', NULL, 1278, '2024-12-20', 0, 'For the treatment of small uncomplicated varicose veins of the lower extremities that show simple dilation with competent valves.', 'Sodium tetradecyl sulfate is a potent toxin for endothelial cells in that brief exposure to even low concentrations are effective in stripping endothelium over a considerable distance and exposing highly thrombogenic endothelium in the process. Diluted sodium tetradecyl sulfate is also able to induce a hypercoagulable state, possibly by selective inhibition of protein C, and can also promote platelet aggregation.', 'Sodium tetradecyl sulfate is an anionic surfactant which occurs as a white, waxy solid. It is used as a sclerosing agent in sclerotherapy. Sclerotherapy is an injection of a sclerosing agent directly through the skin into a lesion and is used primarily for slow-flow vascular anomalies, particularly for venous malformation and lymphatic malformation. Intravenous injection causes intima inflammation and thrombus formation. This usually occludes the injected vein. Subsequent formation of fibrous tissue results in partial or complete vein obliteration that may or may not be permanent.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (23721, 'Normapafenone 300 mg 10 f.c. tabs', 'نورمابافينون 300مجم 10 اقراص', '19', NULL, 'Antiarrhythmias.class ic', 'Propafenone', 'International drug agency (idi) > biocapital pharma', 'Tab', 'أقراص', '300 mg', '1', NULL, 'about propafenone membrane stabilizing agent antiarrhythmic(class ic). indications for propafenone arrhythmia typical dosage for propafenone adults: 150mg 3 times daily and if sufficient response is not obtained dose can be increased to 225mg 3 times dail', NULL, NULL, 958, '2022-07-09', 0, 'Used to prolong the time to recurrence of paroxysmal atrial fibrillation/flutter (PAF) associated with disabling symptoms in patients without structural heart disease. Also used for the treatment of life-threatening documented ventricular arrhythmias, such as sustained ventricular tachycardia.', 'The electrophysiological effect of propafenone manifests itself in a reduction of upstroke velocity (Phase 0) of the monophasic action potential. In Purkinje fibers, and to a lesser extent myocardial fibers, propafenone reduces the fast inward current carried by sodium ions, which is responsible for the drugs antiarrhythmic actions. Diastolic excitability threshold is increased and effective refractory period prolonged. Propafenone reduces spontaneous automaticity and depresses triggered activity. At very high concentrations in vitro, propafenone can inhibit the slow inward current carried by calcium but this calcium antagonist effect probably does not contribute to antiarrhythmic efficacy.', 'Propafenone is a Class 1C antiarrhythmic drug with local anesthetic effects, and a direct stabilizing action on myocardial membranes. It is used in the treatment of atrial and ventricular arrhythmias. It acts by inhibiting sodium channels to restrict the entry of sodium into cardiac cells resulting in reduced excitation. Propafenone has local anesthetic activity approximately equal to procaine.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (23722, 'Normash 400mg 10 f.c. tab.', 'نورماش 400مجم 10 اقراص', '18', NULL, 'Antibiotic.quinolone', 'Norfloxacin', 'Mash premiere', 'Tab', 'أقراص', '400mg', '1', NULL, 'about norfloxacin a synthetic second generation synthetic fluoroquinolone (quinolone) broad spectrum antibiotic. mechanism of action of norfloxacin norfloxacin is a bacteriostatic at low concentration and bactericidal at high concentration. it act by inhi', NULL, NULL, 781, '2022-07-13', 0, 'For the treatment of urinary tract infection', 'The bactericidal action of Norfloxacin results from inhibition of the enzymes topoisomerase II (DNA gyrase) and topoisomerase IV, which are required for bacterial DNA replication, transcription, repair, and recombination. Norfloxacin is a broad-spectrum antibiotic agent that is shown to be effective against various Gram-positive and Gram-negative bacterial species. The fluorine atom at the 6 position increases potency against gram-negative organisms, and the piperazine moiety at the 7 position is responsible for anti-pseudomonal activity', 'Norfloxacin is a quinolone/fluoroquinolone antibiotic. Norfloxacin is bactericidal and its mode of action depends on blocking of bacterial DNA replication by binding itself to an enzyme called DNA gyrase, which allows the untwisting required to replicate one DNA double helix into two. Notably the drug has 100 times higher affinity for bacterial DNA gyrase than for mammalian.', 'DrugBank', 0, 0, 0);
