INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (14873, 'Bacticlor 125mg/5ml susp. 60ml', 'بكتيكلور 125 مجم / 5 مل معلق 60 مل', '73', '51', 'Antibiotic.cephalosporin.second-generation', 'Cefaclor', 'Pharco b > ranbaxy', 'Susp', 'معلق', '125mg', '1', NULL, 'about cefaclor a second-generation cephalosporin antibiotic oral broad-spectrum antibiotics. mechanism of action cefaclor is a 2nd generation cephalosporin which exerts its bactericidal action against both gram positive & gram negative organisms by inhibi', '6224009052141', NULL, 2359, '2024-06-25', 'For the treatment of certain infections caused by bacteria such as pneumonia and ear, lung, skin, throat, and urinary tract infections.', 'Cefaclor, like the penicillins, is a beta-lactam antibiotic. By binding to specific penicillin-binding proteins (PBPs) located inside the bacterial cell wall, it inhibits the third and last stage of bacterial cell wall synthesis. Cell lysis is then mediated by bacterial cell wall autolytic enzymes such as autolysins. It is possible that cefaclor interferes with an autolysin inhibitor.', 'Cefaclor is a second generation cephalosporin antibiotic with a spectrum resembling first-generation cephalosporins. <i>In vitro</i> tests demonstrate that the bactericidal action of the cephalosporins results from inhibition of cell-wall synthesis. As indicated by _in vitro_ and _in vivo_ clinical studies, cefaclor was shown to be effective against most strains of Gram positive aerobes - Staphylococci (including coagulase-positive, coagulase-negative, and penicillinase-producing strains), <i>Streptococcus pneumoniae</i>, <i>Streptococcus pyogenes</i> (group A &szlig;-hemolytic streptococci), as well as Gram-negative aerobes - <i>Escherichia coli</i>, <i>Haemophilus influenzae</i> (including &szlig;-lactamase-producing ampicillin-resistant strains), <i>Klebsiella sp</i>, and <i>Proteus mirabilis</i>.', 'DrugBank', 0, 0, 0);
