INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (18549, 'Eslicarba 400 mg 20 tabs.', 'ايسليكاربا 400 مجم 20 قرص', '208', NULL, 'Anti-epileptic', 'Eslicarbazepine', 'Dbk pharma > genesis pharmaceuticals', 'Tab', 'أقراص', '400 mg', '2', 'يستخدم بمفرده أو مع أدوية أخرى لعلاج نوع معين من اضطرابات الصرع (نوبات بؤرية).فهو يقلل من عدد النوبات.', 'eslicarbazepine is used for: treating partial-onset seizures. it is used along with other medicines. eslicarbazepine is an anticonvulsant. exactly how it works is not known but it may work by slowing abnormal nerve impulses in the brain.', NULL, NULL, 1037, '2022-08-02', 'Eslicarbazepine acetate is indicated for the treatment of partial-onset seizures in patients 4 years of age and older.[L4565]', 'Eslicarbazepine acetate is converted to the active metabolite eslicarbazepine which carries out its anticonvulsant activity. The exact mechanism of action is unknown, but it is thought to involve the inhibition of voltage-gated sodium channels. In in vitro electrophysiological studies, eslicarbazepine was shown to inhibit repeated neuronal firing by stabilizing the inactivated state of voltage-gated sodium channels and preventing their return to the activated state. In vitro studies also showed eslicarbazepine inhibiting T-type calcium channels, which likely also has a role in anticonvulsant activity. ', 'Eslicarbazepine acetate is associated with a dose- and concentration-dependant increase in heart rate and prolongation of PR interval.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (25023, 'Pofol 10mg/ml (20ml) 5 amp. for i.v. inj. or inf.', 'بوفول 10مجم/مل 5 امبول', '140', NULL, 'General anaesthesia', 'Propofol', 'Dong kook pharm co.-korea > eimc', 'Inj', 'حقن', '10mg', '5', NULL, 'about propofol a short-acting intravenous hypnotic general anesthetia(intravenous induction agent). mechanism of action of propofol it excites inhibitory and inhibits excitatory synapsis by binding ligand-gated ion channels specially post-synaptic gaba re', '6222003900048', NULL, 1153, '2022-12-08', 'Used for induction and/or maintenance of anaesthesia and for management of refractory status epilepticus. ', 'The action of propofol involves a positive modulation of the inhibitory function of the neurotransmitter gama-aminobutyric acid (GABA) through GABA-A receptors.', 'Propofol is a sedative-hypnotic agent for use in the induction and maintenance of anesthesia or sedation. Intravenous injection of a therapeutic dose of propofol produces hypnosis rapidly with minimal excitation, usually within 40 seconds from the start of an injection (the time for one arm-brain circulation).', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (31862, 'Rhipgic 5 mg 30 f.c. tabs.', 'ريبجيك 5مجم 30 قرص', '20', NULL, 'Anti-histamine.non-sedative.third-generation', 'Levocetirizine', 'P & c labs', 'Tab', 'أقراص', '5 mg', '3', NULL, 'about levocetirizine a third-generation non-sedative h1 antagonist antihistamine. mechanism of action of levocetirizine this active enantiomer of cetrizine is a potent nonsedative and selective h1 receptor antagonist. it antagonizes the allergic symptoms', NULL, NULL, 663, '2023-05-04', 'Levocetirizine is indicated to treat symptoms of perennial allergic rhinitis and uncomplicated skin manifestations of chronic idiopathic urticaria.[L7694] It is also used over the counter for a variety of mild allergy symptoms.[A181748]', 'Levocetirizine selectively inhibits histamine H<sub>1</sub> receptors.[L7694] This action prevents histamine from activating this receptor and causing effects like smooth muscle contraction, increased permeability of vascular endothelium, histidine uptake in basophils, stimulation of cough receptors, and stimulation of flare responses in the nervous system.[A181790]', 'Levocetirizine is a second generation histamine H<sub>1</sub> antagonist used to treat various allergic symptoms.[A181748,A181790,L7694] It has a long duration of action as it is generally taken once daily, and a wide therapeutic window as animal studies show the maximal nonlethal dose is over 100x a normal dose.[L7694] Patients are cautioned to avoid tasks that require complete alertness, avoid alertness, and use caution in patients with factors predisposing urinary retention.[L7694]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (28542, 'Tritone 200mg 30 tab', 'ترايتون 200مجم 30 قرص', '123', '76.5', 'Antimuscarinic.irritable bowel syndrome', 'Trimebutine', 'Global napi pharmaceuticals', 'Tab', 'أقراص', '200mg', '3', '1- علاج حالات ارتجاع المريء والمعدة.', 'about trimebutine antimuscarinic a noncompetitive spasmolytic agent in irritable bowel syndrome (spastic colon). mechanism of action of trimebutine trimebutine is a noncompetitive spasmolytic agent. it slows down the movement of the bowel and therefore it', '6223002143238', NULL, 13930, '2024-08-19', 'Indicated for symptomatic treatment of irritable bowel syndrome (IBS) and treatment of postoperative paralytic ileus following abdominal surgery. ', 'At high concentrations, trimebutine is shown to inhibit the extracellular Ca2+ influx in the smooth muscle cells through voltage dependent L-type Ca2+ channels and further Ca2+ release from intracellular Ca2+ stores [A19691, A19689]. Trimebutine is suggested to bind to the inactivated state of the calcium channel with high affinity. Reduced calcium influx attenuates membrane depolarization and decrease colon peristalsis. It also inhibits outward K+ currents in response to membrane depolarization of the GI smooth muscle cells at resting conditions through inhibition of delayed rectifier K+ channels and Ca2+ dependent K+ channels, which results in induced muscle contractions [A19691, A19695]. Trimebutine binds to mu opioid receptors with more selectivity compared to delta or kappa opioid receptors but with lower affinity than their natural ligands. Its metabolites (N-monodesmethyl-trimebutine or nor-trimebutine), are also shown to bind to opoid receptors on brain membranes and myenteric synaptosomes [A19692]. ', 'Trimebutine is a spasmolytic agent that acts directly on smooth muscle to modulate gastric motility. It shows a "dual function" that stimulates or inhibits spontaneous contractions depending on the concentration and prior contractile activity in the preparation. Targeting ion conductance that regulates GI motility, trimebutine inhibits the inward calcium currents and calcium-dependent potassium currents in a concentration-dependent manner [A19691]. At lower concentrations (1-10uM), trimebutine depolarizes the resting membrane potential without affecting the amplitude of contractions, which is thought to be mediated by inhibition of outward potassium currents. It is also shown to activate T-type Ca2+ channel and increase gastric emptying, intestinal and colonic contractility [A19690]. At higher concentrations (100-300uM), reduced amplitude of spontaneous contractions and action potentials is thought to be mediated by inhibition of L-type Ca2+ channels and inward calcium current [A19691]. Trimebutine mediates a local anesthetic action by acting as a weak agonist at mu opioid receptors.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (1085, 'Atomoxapex 18mg 30 caps.', 'اتوموكسابكس 18 مجم 30 كبسولة', '171', '120', 'Attention-deficit hyperactivity disorder', 'Atomoxetine', 'Multi-apex', 'Cap', 'كبسولة', '18mg', '3', NULL, NULL, '6223003203320', NULL, 1796, '2025-09-02', 'Atomoxetine is indicated for the treatment of attention deficit hyperactivity disorder (ADHD) in children and adults.', 'Atomoxetine is known to be a potent and selective inhibitor of the norepinephrine transporter (NET),[A175723] which prevents cellular reuptake of norepinephrine throughout the brain, which is thought to improve the symptoms of ADHD. More recently, positron emission tomography (PET) imaging studies in rhesus monkeys have shown that atomoxetine also binds to the serotonin transporter (SERT),[A178111] and blocks the N-methyl-d-aspartate (NMDA) receptor,[A18263] indicating a role for the glutamatergic system in the pathophysiology of ADHD. ', 'Atomoxetine is a selective norepinephrine (NE) reuptake inhibitor used for the treatment of attention deficit hyperactivity disorder (ADHD). Atomoxetine has been shown to specifically increase norepinephrine and dopamine within the prefrontal cortex, which results in improved ADHD symptoms.[A18262, A18262, F4639]



Due to atomoxetine''s noradrenergic activity, it also has effects on the cardiovascular system such as increased blood pressure and tachycardia.[T244] Sudden deaths, stroke, and myocardial infarction have been reported in patients taking atomoxetine at usual doses for ADHD. Atomoxetine should be used with caution in patients whose underlying medical conditions could be worsened by increases in blood pressure or heart rate such as certain patients with hypertension, tachycardia, or cardiovascular or cerebrovascular disease. It should not be used in patients with severe cardiac or vascular disorders whose condition would be expected to deteriorate if they experienced clinically important increases in blood pressure or heart rate. Although the role of atomoxetine in these cases is unknown, consideration should be given to not treating patients with clinically significant cardiac abnormalities. Patients who develop symptoms such as exertional chest pain, unexplained syncope, or other symptoms suggestive of cardiac disease during atomoxetine treatment should undergo a prompt cardiac evaluation.[F4639]



In general, particular care should be taken in treating ADHD in patients with comorbid bipolar disorder because of concern for possible induction of a mixed/manic episode in patients at risk for bipolar disorder. Treatment emergent psychotic or manic symptoms, e.g., hallucinations, delusional thinking, or mania in children and adolescents without a prior history of psychotic illness or mania can be caused by atomoxetine at usual doses. If such symptoms occur, consideration should be given to a possible causal role of atomoxetine, and discontinuation of treatment should be considered.[F4639]



Atomoxetine capsules increased the risk of suicidal ideation in short-term studies in children and adolescents with Attention-Deficit/Hyperactivity Disorder (ADHD). All pediatric patients being treated with atomoxetine  should be monitored appropriately and observed closely for clinical worsening, suicidality, and unusual changes in behavior, especially during the initial few months of a course of drug therapy, or at times of dose changes, either increases or decreases.[F4639]



Postmarketing reports indicate that atomoxetine can cause severe liver injury. Although no evidence of liver injury was detected in clinical trials of about 6000 patients, there have been rare cases of clinically significant liver injury that were considered probably or possibly related to atomoxetine use in postmarketing experience. Rare cases of liver failure have also been reported, including a case that resulted in a liver transplant. Atomoxetine should be discontinued in patients with jaundice or laboratory evidence of liver injury, and should not be restarted. Laboratory testing to determine liver enzyme levels should be done upon the first symptom or sign of liver dysfunction (e.g., pruritus, dark urine, jaundice, right upper quadrant tenderness, or unexplained “flu like” symptoms).[F4639]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (6487, 'Ivy max syrup 120 ml', 'ايفي ماكس شراب 120 مل', '44', '39', 'Anti-cough.antitussive and expectorant', 'Ivy leaves', 'Company', 'Syrup', 'شراب', '120 ml', '1', 'علاج لحالات الكحة و السعال', NULL, NULL, NULL, 2018, '2025-07-26', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (19379, 'Fragmin 5000 i.u./0.2 ml 10 prefilled syringe', 'فراجمين 5000 وحدة دولية/0.2مل 10 سرنجات', '256', NULL, 'Anticoagulant', 'Dalteparin sodium', 'Pfizer', 'Syringe', 'حقنة', '0.2 ml', '10', NULL, NULL, NULL, NULL, 1092, '2022-07-08', 'Dalteparin is used as a prophylaxis for deep-vein thrombosis and pulmonary embolisms in patients undergoing general surgery (e.g., abdominal, gynecologic, urologic), and in patients with acute medical conditions (e.g. cancer, bed rest, heart failure, severe lung disease). It is also used in patients who have severely restricted mobility, which poses a risk for thromboembolic complications. 



Dalteparin is also used concomitantly with aspirin and/or other therapy (e.g., nitrates, β-adrenergic blockers, clopidogrel, platelet glycoprotein [GP] IIb/IIIa-receptor inhibitors) to reduce the risk of acute cardiac ischemic events. The patients who undergo this treatment combination have unstable angina or non-ST-segment elevation/non-Q-wave myocardial infarction (i.e., non-ST-segment elevation acute coronary syndromes).



It is also used in the prevention of clotting during hemodialysis and hemofiltration in connection with acute renal failure or chronic renal insufficiency.', 'Dalteparin potentiates the activity of ATIII, inhibiting the formation of both factor Xa and thrombin. The main difference between dalteparin and unfractionated heparin (UH) is that dalteparin preferentially inactivates factor Xa. As a result, only a slight increase in clotting time [(i.e. activated partial thomboplastin time (APTT)] is observed relative to UH. For this same reason, APTT is not used to monitor the effects of dalteparin except as an indicator for overdosage. ', 'Dalteparin has an antithrombin binding site that is essential for high affinity binding to the plasma protein antithrombin (ATIII). Anti-Xa activity of plasma is used as both as an estimate of clotting activity, and as a basis to determine dosage. Its use should be avoided in patients with a creatinine clearance less than 20mL/min. In these patients, unfractionated heparin should only be used. As for monitoring, active partial thromboplastin time (aPTT) will only increase at high doses of low molecular weight heparins (LMWH). Therefore, monitoring aPTT is not recommended. However, anti-Xa activity can be measured to monitor the efficacy of the LMWH. ', 'DrugBank', 0, 0, 0);
