INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (9313, 'Optocetine 0.5% eye drops', 'اوبتوسيتين 0.5% قطرة للعين', '4', NULL, 'Antibiotic', 'Chloramphenicol', 'Cid', 'Drops', 'نقط', '0.5%', '1', NULL, 'description an antibiotic first isolated from cultures of streptomyces venequelae in 1947 but now produced synthetically. it has a relatively simple structure and was the first broad-spectrum antibiotic to be discovered. it acts by interfering with bacter', '6221043011578', NULL, 816, '2023-04-28', 'Used in treatment of cholera, as it destroys the vibrios and decreases the diarrhea. It is effective against tetracycline-resistant vibrios. It is also used in eye drops or ointment to treat bacterial conjunctivitis.', 'Chloramphenicol is lipid-soluble, allowing it to diffuse through the bacterial cell membrane. It then reversibly binds to the L16 protein of the 50S subunit of bacterial ribosomes, where transfer of amino acids to growing peptide chains is prevented (perhaps by suppression of peptidyl transferase activity), thus inhibiting peptide bond formation and subsequent protein synthesis.', 'Chloramphenicol is a broad-spectrum antibiotic that was derived from the bacterium Streptomyces venezuelae and is now produced synthetically. Chloramphenicol is effective against a wide variety of microorganisms, but due to serious side-effects (e.g., damage to the bone marrow, including aplastic anemia) in humans, it is usually reserved for the treatment of serious and life-threatening infections (e.g., typhoid fever). Chloramphenicol is bacteriostatic but may be bactericidal in high concentrations or when used against highly susceptible organisms. Chloramphenicol stops bacterial growth by binding to the bacterial ribosome (blocking peptidyl transferase) and inhibiting protein synthesis.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (1934, 'Calmalgine 10 tab', 'كالماجين 10 اقراص', '4', NULL, 'Cold drugs', 'Chlorpheniramine-paracetamol', 'Misr', 'Tab', 'أقراص', NULL, '1', 'علاج نزلات البرد -- تقليل اعراض البرد مثل العطس و الرشح -- تقليل الكحة', NULL, NULL, NULL, 1884, '2022-04-09', NULL, NULL, NULL, NULL, 0, 0, 0);
