INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (733, 'Anexate 0.5mg/5ml 5 i.v. amps.', 'انيكسات 0.5مجم/5مل 5 امبولات وريد', '2,045', '1100', 'Antidote.benzodiazepine overdoses.gaba modulators', 'Flumazenil 100 mcg / ml', 'EGYPTIAN PHARMACEUTICAL TRADING COMPANY', 'Amp', 'أمبول', '0.5mg', '5', 'يستخدم في حالات فرط تناول البنزوديازبيين', 'flumazenil is indicated for the complete or partial reversal of the central sedative effects of benzodiazepines. it may therefore be used in anaesthesia and in the intensive care in the following situations: in anaesthesia: - termination of hypnosedative', NULL, NULL, 2638, '2023-11-09', 'For the complete or partial reversal of the sedative effects of benzodiazepines in cases where general anesthesia has been induced and/or maintained with benzodiazepines, and where sedation has been produced with benzodiazepines for diagnostic and therapeutic procedures. Also for the management of benzodiazepine overdose as an adjunct for appropriate supportive and symptomatic measures.', 'Flumazenil, an imidazobenzodiazepine derivative, is a benzodiazepine antagonist. It competitively inhibits the benzodiazepine binding site on the GABA/benzodiazepine receptor complex. Flumazenil is a weak partial agonist in some animal models of activity, but has little or no agonist activity in man.', 'Flumazenil antagonizes the CNS effects produced by benzodiazepines, but does not antagonize the central nervous system effects of drugs affecting GABA-ergic neurons by means other than the benzodiazepine receptor (including ethanol, barbiturates, or general anesthetics) and does not reverse the effects of opioids.', 'DrugBank', 0, 0, 0);
