INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (20617, 'Hydroxocobalamin 1000 mcg 5 i.m. amp', 'هيدروكسوكوبالامين 1000 مكجم 5 امبولات', '16', NULL, 'Vitamin b', 'Hydroxocobalamin', 'Pharco b', 'Amp', 'أمبول', '1000 mcg', '5', 'آلام العمود الفقري.', NULL, '6222012229697', NULL, 951, '2022-08-16', 'For treatment of pernicious anemia and the prevention and treatment of vitamin B12 deficiency arising from alcoholism, malabsorption, tapeworm infestation, celiac, hyperthyroidism, hepatic-biliary tract disease, persistent diarrhea, ileal resection, pancreatic cancer, renal disease, prolonged stress, vegan diets, macrobiotic diets or other restrictive diets. Also for the treatment of known or suspected cyanide poisoning.', 'Vitamin B12 exists in four major forms referred to collectively as cobalamins; deoxyadenosylcobalamin, methylcobalamin, hydroxocobalamin, and cyanocobalamin. Two of these, methylcobalamin and 5-deoxyadenosyl cobalamin, are primarily used by the body. Methionine synthase needs methylcobalamin as a cofactor. This enzyme is involved in the conversion of the amino acid homocysteine into methionine. Methionine in turn is required for DNA methylation. 5-Deoxyadenosyl cobalamin is a cofactor needed by the enzyme that converts L-methylmalonyl-CoA to succinyl-CoA. This conversion is an important step in the extraction of energy from proteins and fats. Furthermore, succinyl CoA is necessary for the production of hemoglobin, the substances that carries oxygen in red blood cells.', 'Hydroxocobalamin is a synthetic, injectable form of Vitamin B12. Hydroxocobalamin is actually a precursor of two cofactors or vitamins (Vitamin B12 and Methylcobalamin) which are involved in various biological systems in man. Vitamin B12 is required for the conversion of methylmalonate to succinate. Deficiency of this enzyme could therefore interfere with the production of lipoprotein in myelin sheath tissue and so give rise to neurological lesions. The second cofactor, Methylcobalamin, is necessary for the conversion of homocysteine to methionine which is essential for the metabolism of folic acid. Deficiency of tetrahydrafolate leads to reduced synthesis of thymidylate resulting in reduced synthesis of DNA which is essential for cell maturation. Vitamin B12 is also concerned in the maintenance of sulphydryl groups in reduced form, deficiency leading to decreased amounts of reduced SH content of erythrocytes and liver cells. Overall, vitamin B12 acts as a coenzyme for various metabolic functions, including fat and carbohydrate metabolism and protein synthesis. It is necessary for growth, cell replication, hematopoiesis, and nucleoprotein as well as myelin synthesis. This is largely due to its effects on metabolism of methionine folic acid, and malonic acid.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (31032, 'Oneferol bone 2800 i.u./ml oral drops 30 ml', 'اوني فيرول بون 2800 وحدة/مل نقط للفم 30 مل', '46', '39', 'Vitamin d', 'Cholecalciferol(vitamin d3)', 'Pharma one', 'Drops', 'نقط', '30 ml', '1', NULL, 'description derivative of 7-dehydroxycholesterol formed by ultraviolet rays breaking of the c9-c10 bond. it differs from ergocalciferol in having a single bond between c22 and c23 and lacking a methyl group at c24 indication for the treatment of vitamin d', NULL, NULL, 815, '2024-04-26', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (1228, 'Alphaculation 30 tabs', 'الفاكيوليشن 30 قرص', '240', '225', 'Anti-rheumatic -osteoarthritis- anabolic agents', 'Glucosamine sulphate+collagen+chondroitin sulphate+methyl sulfonyl methane (msm)+ginkgo biloba+manganese+copper+vitamin c+vitamin d3', 'Alphabet pharma', 'Tab', 'أقراص', NULL, '3', NULL, NULL, NULL, NULL, 782, '2025-11-13', 'Glucosamine is generally used over the counter in the symptomatic treatment of osteoarthritis and joint pain,[A2352] frequently combined with chondroitin sulfate and/or ibuprofen.[L32699]', 'The mechanism of action of glucosamine in joint health is unclear,[A231894] however there are several possible mechanisms that contribute to its therapeutic effects. Because glucosamine is a precursor for glycosaminoglycans, and glycosaminoglycans are a major component of joint cartilage,[A232289] glucosamine supplements may help to rebuild cartilage and treat the symptoms of arthritis.  Some in vitro studies show evidence that glucosamine reduces inflammation via inhibition of interferon gamma[A12619,A12616,A12621]  and Nuclear factor kappa B subunit 65 (NF-κB p65),[A12616,A232284] improving the symptoms of arthritis and joint pain. Clinical relevance is unknown at this time.', 'The administration of glucosamine, in theory, provides a building block towards the synthesis of glycosaminoglycans, slowing the progression of osteoarthritis and relieving symptoms of joint pain. Studies to this date examining the efficacy of glucosamine sulfate have been inconclusive. Glycosaminoglycans contribute to joint cartilage elasticity, strength, and flexibility.[A232144] A systematic review of various studies and guidelines determined that modest improvements were reported for joint pain and function in patients taking glucosamine. A consistent joint space narrowing was observed, but with an unclear clinical significance.[A231894]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (27369, 'Spectracefocure 300 mg 10 caps.', 'سبيكتراسيفوكيور 300مجم 10 كبسول', '45', NULL, 'Antibiotic.cephalosporin.third-generation', 'Cefdinir', 'Kahira > evercure company-egypt', 'Cap', 'كبسولة', '300 mg', '1', NULL, 'about cefdinir semisynthetic (third generation ) cephalosporin antibiotic oral broad-spectrum antibiotic. mechanism of action cefdinir is a 3rd generation cephalosporin which exerts its bactericidal action against both gram positive & aerobic gram negativ', NULL, NULL, 1043, '2022-07-23', 'Cefdinir is indicated to treat acute bacterial otitis media, acute maxillary sinusitis, community-acquired (CA) pneumonia, acute bacterial exacerbations of chronic bronchitis, pharyngitis/tonsillitis, and uncomplicated skin and skin structure infections in children and adults.[L7330,A180739]



The organisms susceptible to cefdinir have been listed below in addition to their associated clinical condition that may be treated with cefdinir.[L7330] Various beta-lactamase producing organisms may be treated, as indicated in certain sections below.



**Respiratory**

Acute bacterial exacerbations of chronic bronchitis caused by Haemophilus influenzae, Haemophilus parainfluenzae, Streptococcus pneumoniae (penicillin-susceptible only), and Moraxella catarrhalis



Community-acquired pneumonia caused by Haemophilus influenzae, Haemophilus parainfluenzae, Streptococcus pneumoniae (penicillin-susceptible only), and Moraxella catarrhalis



**Ear, nose, and throat** 

Acute bacterial otitis media caused by Haemophilus influenzae, Moraxella catarrhalis, and Streptococcus pneumoniae (penicillin-susceptible only)



Tonsillitis caused by Streptococcus pyogenes



Pharyngitis caused by Streptococcus pyogenes



Acute maxillary sinusitis caused by Haemophilus pneumoniae and Streptococcus pneumoniae (penicillin-susceptible only), and Moraxella catarrhalis



**Skin and skin structure infections**



Uncomplicated skin and skin structure infections caused by Staphylococcus aureus and Streptococcus pyogenes', 'Five-member thiazolidine rings that make up penicillins are replaced in cephalosporins by a six-member dihydrothiazine ring, conferring greater bactericidal activity. This This 6-member ring enables cefdinir and other cephalosporins to resist inactivation by certain bacterial enzymes.[A180772]



With a mechanism similar to other beta-lactam antibiotics, the bactericidal activity of cefdinir is caused by the inhibition of cell wall synthesis via binding to penicillin-binding proteins (PBPs). Cefdinir, like other cephalosporins, penetrates the bacterial cell wall, combats inactivation by beta-lactamase enzymes, and inactivates penicillin-binding proteins.[A180772] This interferes with the final step of transpeptidation in cell walls, eventually leading to cell lysis, which eventually leads to the death of bacteria that are susceptible to this drug.[A180724] Cefdinir has shown affinity to penicillin protein binding proteins 2 and 3. [A15746,A180760,A15747] It has also been shown to inhibit transpeptidase enzymes of various bacteria, which may play a role in its bactericidal action.[A180766,A180769] One in vitro study suggests that cefdinir inhibits myeloperoxidase release extracellularly.[A11897] The impact of this potential drug target in relation to its mechanism of action is unknown.', 'Cefdinir is a bactericidal agent that treats bacterial infections by interfering with cell wall synthesis.[L7330]

Cefdinir exerts broad-spectrum activity against a variety of gram-positive and gram-negative bacterial infections. It is effective against several beta-lactamase enzyme producing bacteria. As a result, many organisms that are resistant to other cephalosporins may be susceptible to cefdinir.[A180727,A180733,L7351]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (18896, 'Feldene 10mg 20 dispersible .tab', 'فلدين 10مجم 20 قرص سريع الذوبان', '29', NULL, 'Nsaid.oxicam', 'Piroxicam', 'Pfizer', 'Tab', 'أقراص', '10mg', '2', NULL, NULL, '6221180000343', NULL, 1149, '2022-12-05', 'For treatment of osteoarthritis and rheumatoid arthritis.', 'The antiinflammatory effect of Piroxicam may result from the reversible inhibition of cyclooxygenase, causing the peripheral inhibition of prostaglandin synthesis. The prostaglandins are produced by an enzyme called Cox-1. Piroxicam blocks the Cox-1 enzyme, resulting into the disruption of production of prostaglandins. Piroxicam also inhibits the migration of leukocytes into sites of inflammation and prevents the formation of thromboxane A2, an aggregating agent, by the platelets.', 'Piroxicam is in a class of drugs called nonsteroidal anti-inflammatory drugs (NSAIDs). Piroxicam works by reducing hormones that cause inflammation and pain in the body. Piroxicam is used to reduce the pain, inflammation, and stiffness caused by rheumatoid arthritis and osteoarthritis.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (952, 'Aripiprex 10mg 30 f.c.tab.', 'اريبيبركس 10مجم 30 قرص', '162', '120', 'Psychiatric.antipsychotics', 'Aripiprazole', 'Andalous pharma', 'Tab', 'أقراص', '10mg', '3', 'علاج الامراض النفسية', 'about aripiprazole second-generation atypical antipsychotic dopamine-serotonin stabilizer. mechanism of action of aripiprazole the mechanism of action of aripiprazole in the treatment of schizophrenia and bipolar disorder is unknown. the proposed mechanis', '6224000396183', NULL, 3599, '2025-03-03', 'Aripiprazole is indicated for the treatment of acute manic and mixed episodes associated with bipolar I disorder, irritability associated with autism spectrum disorder, schizophrenia, and Tourette''s disorder.[L45859] It is also used as an adjunctive treatment of major depressive disorder.[L45859 An injectable formulation of aripiprazole is indicated for agitation associated with schizophrenia or bipolar mania.[L45859] Finally, an extended-release, bimonthly injection formulation of aripiprazole is indicated for the treatment of adult schizophrenia and maintenance therapy for adult bipolar I disorder.[L46203]', 'The antipsychotic action of aripiprazole is likely due to its partial agonist activity on D2 and 5-HT<sub>1A</sub> receptors as well as its antagonist activity at 5-HT<sub>2A</sub> receptors; however, the exact mechanism has not been fully elucidated.[A4393,L45859,L4620] One of the mechanisms that have been proposed is that aripiprazole both stimulates and inhibits dopamine as it engages the D2 receptor. It lowers dopamine neuronal firing at high dopamine concentrations and increases dopamine firing at low concentrations. Its partial agonist activity gives aripiprazole an intermediate level of dopaminergic neuronal tone between full agonist and antagonist of the D2 receptor.[A258699] In addition, some adverse effects may be due to action on other receptors.[L4620] For example, orthostatic hypotension may be explained by antagonism of the adrenergic alpha-1 receptors.[L4620,A4393]', 'Aripiprazole exhibits high affinity for dopamine D<sub>2</sub> and D<sub>3</sub>, serotonin 5-HT<sub>1a</sub> and 5-HT<sub>2a</sub> receptors (Ki values of 0.34 nM, 0.8 nM, 1.7 nM, and 3.4 nM, respectively), moderate affinity for dopamine D<sub>4</sub>, serotonin 5-HT<sub>2c</sub> and 5-HT<sub>7</sub>, alpha<sub>1</sub>-adrenergic and histamine H<sub>1</sub> receptors (Ki values of 44 nM, 15 nM, 39 nM, 57 nM, and 61 nM, respectively), and moderate affinity for the serotonin reuptake site (Ki=98 nM). Aripiprazole has no appreciable affinity for cholinergic muscarinic receptors (IC<sub>50</sub>>1000 nM).[L45859]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (20470, 'Hiderm topical lotion 100ml', 'هاي ديرم لوسيون 100 مل', '55', '48', 'Moisturizing topicals', 'Glycerin+zinc oxide+olive oil+menthol+titanium dioxide+lanolin', 'Hi-care > avano pharma', 'Lotion', 'لوشن', '100ml', '1', NULL, 'indications : prickly heat rash (sweat rash). inflamed skin associated with itching. skin urticaria. insect bits. relieving symptoms associated with chicken pox. skin dryness in babies.', '6224000201449', NULL, 4237, '2025-01-27', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (15942, 'Catapres(catapresan) 100 mcg 100 tabs.(illegal import)', 'كاتابريس 100 ميكروجرام 100 قرص', '0', NULL, 'Alpha2 adrenergic agonist', 'Clonidine hydrochloride', 'Boehringer ingelheim', 'Tab', 'أقراص', '100 mcg', '1', NULL, 'indication may be used as an adjunct in the treatment of hypertension as an epidural infusion as an adjunct treatment in the management of severe cancer pain that is not relieved by opiate analgesics alone for differential diagnosis of pheochromocytoma in', NULL, NULL, 1033, '2022-07-12', 'Clonidine tablets and transdermal systems are indicated for the treatment of hypertension alone or in combination with other medications.[L7237,L7240] A clonidine injection is indicated for use with opiates in the treatment of severe cancer pain where opiates alone are insufficient.[L7243] An extended release tablet of clonidine is indicated for the treatment of ADHD either alone or in combination with other medications.[L7246]



Clonidine is also used for the diagnosis of pheochromocytoma,[A180565] treatment of nicotine dependance,[A180568] and opiate withdrawal.[A180571]', 'Clonidine is primarily an alpha-2 adrenoceptor agonist which causes central hypotensive and anti-arrhythmogenic effects.[A180559] The alpha-2 adrenoceptor is coupled to the G-proteins G<sub>o</sub> and G<sub>i</sub>.[A180559] G<sub>i</sub> inhibits adenylyl cyclase and activates opening of a potassium channel that causes hyperpolarization.[A180559] Clonidine binding to the alpha-2 adrenoceptor causes structural changes in the alpha subunit of the G-protein, reducing its affinity for GDP.[A180559] Magnesium catalyzes the replacement of GDP with GTP.[A180559] The alpha subunit dissociates from the other subunits and associates with an effector.[A180559]



The stimulation of alpha-2 adrenoceptors in the locus coeruleus may be responsible for the hypnotic effects of clonidine as this region of the brain helps regulate wakefulness.[A180559] Clonidine can also decrease transmission of pain signals at the spine.[A180559] Finally clonidine can affect regulators of blood pressure in the ventromedial and rostral-ventrolateral areas of the medulla.[A180559]', 'Clonidine functions through agonism of alpha-2 adrenoceptors which have effects such as lowering blood pressure, sedation, and hyperpolarization of nerves.[A180559] It has a long duration of action as it is given twice daily and the therapeutic window is between 0.1mg and 2.4mg daily.[L7237,L7240,L7243,L7246]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (33121, 'Lornicam 4 mg 30 tabs.', 'لورنيكام 4مجم 30 قرص', '66', '31.5', 'Nsaid.oxicam', 'Lornoxicam', 'Mira international', 'Tab', 'أقراص', '4 mg', '3', NULL, 'about lornoxicam non-steroidal anti-inflammatory drug (nsaid) of the oxicam class with analgesic (pain relieving) anti-inflammatory and antipyretic (fever reducing) properties. it is available in oral and parenteral formulations. mechanism of action of lo', '6224009669370', NULL, 737, '2024-11-30', 'For the treatment of acute mild to moderate pain, as well as pain and inflammation of the joints caused by certain types of rheumatic diseases.', 'Like other NSAIDS, lornoxicam''s anti-inflammatory and analgesic activity is related to its inhibitory action on prostaglandin and thromboxane synthesis through the inhibition of both COX-1 and COX-2. This leads to the reduction of inflammation, pain, fever, and swelling, which are mediated by prostaglandins. However, the exact mechanism of lornoxicam, like that of the other NSAIDs, has not been fully determined.', 'Lornoxicam is a non-steroidal anti-inflammatory drug (NSAID) that belongs to the oxicam class. As with other NSAIDS, lornoxicam is a potent inhibitor of the cyclooxgenase enzymes, which are responsible for catalyzing the formation of prostaglandins (act as messenger molecules in the process of inflammation) and thromboxane from arachidonic acid. Unlike some NSAIDS, lornoxicam''s inhibition of cyclooxygenase does not lead to an increase in leukotriene formation, meaning that arachidonic acid is not moved to the 5-lipoxygenase cascade, resulting in the minimization of the risk of adverse events.', 'DrugBank', 0, 0, 0);
