INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (4432, 'Ethambutol memphis 500 mg 20 f.c. tab.', 'ايثامبيوتول ممفيس 500مجم 20 قرص', '9', NULL, 'Antitubercular', 'Ethambutol', 'Memphis', 'Tab', 'أقراص', '500 mg', '2', NULL, NULL, NULL, NULL, 2038, '2022-08-02', 'Ethambutol is indicated in combination with other anti-tuberculosis drugs in the treatment of pulmonary tuberculosis.[L31663] Ethambutol is commonly used in combination with [isoniazid], [rifampin], and [pyrazinamide].[L31743]', 'Ethambutol diffuses into _Mycobacterium_ cells.[L31663] Once inside the cell, ethambutol inhibits the arabinosyltransferases (embA, embB, and embC), preventing formation of the cell wall components arabinogalactan and lipoarabinomannan, and preventing cell division.[A228973,A4616,A229043,A228968] Decreased concentrations of arabinogalactan in the cell wall reduces the number of binding sites for mycolic acid, leading to the accumulation of mycolic acid, trehalose monomycolate, and trehalose dimycolate.[A229028,A4616] Lipoarabinomannan is a component of a cell surface molecule involved in the interaction with host cells.[A4616] Reduced levels of lipoarabinomannan may interfere with mycobacterial interaction with host cells.[A4616]', 'Ethambutol is indicated in combination with other anti-tuberculosis drugs in the treatment of pulmonary tuberculosis.[L31663] It has a long duration of action as it is administered daily, and a moderate therapeutic window.[L31663] Patients should be counselled regarding the risk of optic neuritis and hepatic toxicity.[L31663]', 'DrugBank', 0, 0, 0);
