INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (24239, 'Orchazid 0.025% eye drops 30*0.4 ml sdu', 'اوركازيد قطرة عين 30*0.4 مل وحدات مفردة', '60', NULL, 'Antihistamine.second-generation', 'Ketotifen', 'Orchidia pharmaceutical industries', 'Drops', 'نقط', '0.025%', '1', NULL, 'about ketotifen second generation h1 antagonist mast cell stabilizer antihistamine. mechanism of action of ketotifen it is a mast cell stabilizer. this inhibit the development of airway hyper reactivity (associated with activation of platelets by paf (pla', '6224000219840', NULL, 948, '2022-07-01', 0, 'Administered orally, ketotifen is indicated as an add-on medication in the chronic treatment of mild atopic asthma in children.[L32283] It is also available as an over-the-counter ophthalmic solution which is indicated for the temporary prevention of itching of the eye due to allergic conjunctivitis.[L32278]', 'The precise mechanism(s) through which ketotifen exerts its therapeutic effects are unclear. Ketotifen is a potent and non-competitive antagonist of H1 histamine receptors, which is likely to be a significant contributor to its anti-allergic activity.[L32283] In addition, ketotifen stabilizes mast cells and has demonstrated _in vitro_ the ability to inhibit the release of allergic and inflammatory mediators such as histamine, leukotrienes C<sub>4</sub> and D<sub>4</sub> (i.e. SRS-A), and platelet-activating factor (PAF).[L32283]



Other _in vivo_ observations thought to contribute to ketotifen''s efficacy in asthma include the inhibition of various PAF-mediated processes (e.g. airway hyperreactivity, eosinophil and platelet accumulation in the airways), prevention of leukotriene-induced bronchoconstriction, and suppression of eosinophil priming.[L32283]', 'Ketotifen is a non-competitive histamine antagonist and mast cell stabilizer.[L32278] Administered orally, it functions as a non-bronchodilator antiasthmatic drug by inhibiting the effects of endogenous substances known to be inflammatory mediators.[L32283] While effects can take 6 to 12 weeks to become apparent,[A231214] the use of ketotifen has been demonstrated to reduce the frequency, severity, and duration of asthma symptoms, and may allow for a reduction in the use of other asthma therapies.[L32283]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (24240, 'Orchinohist 0.25% eye dps. 3 ml', 'اوركينوهيست قطرة 0.25% عبوة 3 مل', '53', '45', 'Antihistamine.second-generation', 'Alcaftadine', 'Orchidia pharmaceutical industries', 'Unknown', 'غير محدد', '0.25%', '1', 'مضادة للحساسية', 'second generation h1 antagonist mast cell stabilizer antihistamine. alcaftadine is a h1 histamine receptor antagonist indicated for the prevention of itching associated with allergic conjunctivitis. this drug was approved in july 2010. alcaftadine is a h1', '6223005440013', NULL, 2498, '2025-08-04', 0, 'For the prevention of itching associated with allergic conjunctivitis.', 'Alcaftadine is a H1 histamine receptor antagonist and inhibitor of the release of histamine from mast cells. Decreased chemotaxis and inhibition of eosinophil activation has also been demonstrated.', 'Following bilateral topical ocular administration of alcaftadine ophthalmic solution, 0.25%, the mean plasma Cmax of alcaftadine was approximately 60 pg/mL and the median Tmax occurred at 15 minutes. Plasma concentrations of alcaftadine were below the lower limit of quantification (10 pg/mL) by 3 hours after dosing. The mean Cmax of the active carboxylic acid metabolite was approximately 3 ng/mL and occurred at 1 hour after dosing. Plasma concentrations of the carboxylic acid metabolite were below the lower limit of quantification (100 pg/mL) by 12 hours after dosing.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (24241, 'Ordesca 2.5mg/5ml syp. 120 ml', 'اورديسكا 2.5مجم/5مل شراب 120مل', '9', NULL, 'Antihistamine.antiallergy', 'Desloratadine', 'Rameda', 'Unknown', 'غير محدد', '2.5mg', '1', NULL, 'about desloratadine second generation long-acting tricyclic h1 antagonist antihistamine. mechanism of action of desloratadine this second generation antihistamine has a selective peripheral h1-antagonist action.desloratadine competes with free histamine f', NULL, NULL, 742, '2022-07-24', 0, 'For the relief of symptoms of seasonal allergic rhinitis, perennial (non-seasonal) allergic rhinitis. Desloratidine is also used for the sympomatic treatment of pruritus and urticaria (hives) associated with chronic idiopathic urticaria.', 'Like other H1-blockers, Desloratadine competes with free histamine for binding at H<sub>1</sub>-receptors in the GI tract, uterus, large blood vessels, and bronchial smooth muscle. This blocks the action of endogenous histamine, which subsequently leads to temporary relief of the negative symptoms (eg. nasal congestion, watery eyes) brought on by histamine.', 'Desloratadine is a long-acting second-generation H<sub>1</sub>-receptor antagonist which has a selective and peripheral H1-antagonist action. Histamine is a chemical that causes many of the signs that are part of allergic reactions, such as the swelling of tissues. Histamine is released from histamine-storing cells (mast cells) and attaches to other cells that have receptors for histamine. The attachment of the histamine to the receptors causes the cell to be "activated," releasing other chemicals which produce the effects that we associate with allergies. Desloratadine blocks one type of receptor for histamine (the H1 receptor) and thus prevents activation of cells by histamine. Unlike most other antihistamines, Desloratadine does not enter the brain from the blood and, therefore, does not cause drowsiness.', 'DrugBank', 0, 0, 0);
