INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (17397, 'Desofective 0.05% topical cream 15 gm', 'ديسوفيكتيف 0.05% كريم 15 جم', '10', NULL, 'Glucocorticoid', 'Desonide', 'Egpi > rector pharma', 'Cream', 'كريم', '0.05%', '1', NULL, 'non flourinated corticosteroid for topical dermatological use. it has anti-inflammatory antipruritic and vasoconstrictive effects.', NULL, NULL, 675, '2022-07-08', 'For the relief of the inflammatory and pruritic manifestations of corticosteroid responsive dermatose.', 'Like other topical corticosteroids, desonide has anti-inflammatory, antipruritic and vasoconstrictive properties. The drug binds to cytosolic glucocorticoid receptors. This complex migrates to the nucleus and binds to genetic elements on the DNA. This activates and represses various genes. However corticosteroids are thought to act by the induction of phospholipase A2 inhibitory proteins, collectively called lipocortins. It is postulated that these proteins control the biosynthesis of potent mediators of inflammation such as prostaglandins and leukotrienes by inhibiting the release of their common precursor arachidonic acid. Arachidonic acid is released from membrane phospholipids by phospholipase A2.', 'Desonide is a synthetic nonfluorinated corticosteroid for topical dermatologic use. The corticosteroids constitute a class of primarily synthetic steroids used topically as anti-inflammatory and antipruritic agents.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (18972, 'Ferosac 100mg/5ml 5 amp.', 'فيروساك 100مجم/5مل 5 امبولات', '470', '325', 'Anemia.iron supplement', 'Ferric hydroxide saccharate complex', 'Spimaco > eimc', 'Amp', 'أمبول', '100mg', '5', 'علاج حالات الانيميا ونقص الحديد', NULL, '6281086040768', NULL, 24505, '2025-02-18', 'Ferric carboxymaltose is an iron replacement product indicated for the treatment of iron deficiency anemia in patients ≥1 year of age who have an intolerance to, or unsatisfactory response from, oral iron therapy,[L49379] and in adult patients who have non-dialysis-dependent chronic kidney disease.[L49379] It is also indicated to treat iron deficiency and improve exercise capacity in adult patients with NYHA class II or III heart failure.[L49379]', 'Ferric carboxymaltose is a colloidal iron (III) hydroxide in complex with carboxymaltose, a carbohydrate polymer that releases iron.[L49379]', 'When measured using positron emission tomography (PET), the red cell uptake of 59-Fe and 52-Fe from INJECTAFER ranged from 61% to 99%.[L49379] In patients with iron deficiency, the red cell uptake ranged from 91% to 99%.[L49379] In patients with renal anemia, the red cell uptake ranged from 61% to 84%.[L49379]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (23410, 'Nepiralin 200 mg 10 tab.', 'نبيرالين 200مجم 10 اقراص', '90', NULL, 'Antiviral.nucleoside', 'Nevirapine', 'Egpi', 'Tab', 'أقراص', '200 mg', '1', NULL, 'about nevirapine a non-nucleoside reverse transcriptase inhibitor (nnrti) antiretroviral. mechanism of action of nevirapine nevirapine noncompetitively binds to a site other than catalytic site in the hiv- 1 reverse transcriptase enzyme .this binding will', NULL, NULL, 727, '2022-07-16', 'For use in combination with other antiretroviral drugs in the ongoing treatment of HIV-1 infection.', 'Nevirapine binds directly to reverse transcriptase (RT) and blocks the RNA-dependent and DNA-dependent DNA polymerase activities by causing a disruption of the enzyme''s catalytic site. The activity of nevirapine does not compete with template or nucleoside triphosphates.', 'Nevirapine is a non-nucleoside reverse transcriptase inhibitor (nNRTI) with activity against Human Immunodeficiency Virus Type 1 (HIV-1). HIV-2 RT and eukaryotic DNA polymerases (such as human DNA polymerases alpha, beta, or sigma) are not inhibited by nevirapine. Nevirapine is, in general, only prescribed after the immune system has declined and infections have become evident. It is always taken with at least one other HIV medication such as Retrovir or Videx. The virus can develop resistance to nevirapine if the drug is taken alone, although even if used properly, nevirapine is effective for only a limited time.', 'DrugBank', 0, 0, 0);
