INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (8691, 'Neostigmine methyl sulphate 0.5mg/ml 5 amp.', 'نيوستجمين ميثيل سالفات 0.5مجم/مل 5 امبولات', '35', '20', 'Para-sympathomimetic', 'Neostigmine', 'Amriya', 'Amp', 'أمبول', '0.5mg', '5', NULL, 'about neostigmine para-sympathomimetic a reversible anticholinesterase in myasthenia gravis treatment an adjunct in general anaesthesia curare antagonist. mechanism of action of neostigmine it inhibits the destruction of acetylcholine by cholinesterase (b', '6221075030066', NULL, 1511, '2024-11-11', 'Neostigmine is used for the symptomatic treatment of myasthenia gravis by improving muscle tone.', 'Neostigmine is a parasympathomimetic, specifically, a reversible cholinesterase inhibitor. The drug inhibits acetylcholinesterase which is responsible for the degredation of acetylcholine. So, with acetylcholinesterase inhibited, more acetylcholine is present By interfering with the breakdown of acetylcholine, neostigmine indirectly stimulates both nicotinic and muscarinic receptors which are involved in muscle contraction.. It does not cross the blood-brain barrier.', 'Neostigmine is a cholinesterase inhibitor used in the treatment of myasthenia gravis and to reverse the effects of muscle relaxants such as gallamine and tubocurarine. Neostigmine, unlike physostigmine, does not cross the blood-brain barrier. By inhibiting acetylcholinesterase, more acetylcholine is available in the synapse, therefore, more of it can bind to the fewer receptors present in myasthenia gravis and can better trigger muscular contraction.



', 'DrugBank', 0, 0, 0);
