INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (24088, 'Omnipaque 300mg i/ml (100ml) intravascular/intrathecal vial', 'اومنيباك 300مجم/مل فيال 100 مل', '244', NULL, 'Radiographic procedures.contrast agent', 'Iohexol', 'Amersham health - ireland > new technology', 'Vial', 'فيال', '300mg', '1', NULL, 'description iohexol is an effective non-ionic water-soluble contrast agent which is used in myelography arthrography nephroangiography arteriography and other radiographic procedures. its low systemic toxicity is the combined result of low chemotoxicity a', NULL, NULL, 824, '2022-07-09', 'Iohexol ia used in myelography, arthrography, nephroangiography, arteriography, and other radiographic procedures.', 'Organic iodine compounds block x-rays as they pass through the body, thereby allowing body structures containing iodine to be delineated in contrast to those structures that do not contain iodine. The degree of opacity produced by these compounds is directly proportional to the total amount (concentration and volume) of the iodinated contrast agent in the path of the x-rays. After intrathecal administration into the subarachnoid space, diffusion of iohexol in the CSF allows the visualization of the subarachnoid spaces of the head and spinal canal. After intravascular administration, iohexol makes opaque those vessels in its path of flow, allowing visualization of the internal structures until significant hemodilution occurs.', 'Iohexol is an effective non-ionic, water-soluble contrast agent which is used in myelography, arthrography, nephroangiography, arteriography, and other radiographic procedures. Its low systemic toxicity is the combined result of low chemotoxicity and low osmolality.', 'DrugBank', 0, 0, 0);
