INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (2901, 'Colircusi cicloplejico 1% eye drops 10 ml', 'كولير كوزي سيكلوبلجيكو 1% قطرة عين 10 مل', '19', NULL, 'Mydriatic', 'Cyclopentolate', 'Novartis scientific office', 'Drops', 'نقط', '1%', '1', NULL, NULL, NULL, NULL, 1076, '2022-07-22', 'Used mainly to produce mydriasis and cycloplegia for diagnostic purposes.', 'By blocking muscarinic receptors, cyclopentolate produces dilatation of the pupil (mydriasis) and prevents the eye from accommodating for near vision (cycloplegia).', 'Cyclopentolate is an anti-muscarinic in the same class as atropine and scopolamine. Cyclopentolate blocks the receptors in the muscles of the eye (muscarinic receptors). These receptors are involved controlling the pupil size and the shape of the lens. Cyclopentolate thus induces relaxation of the sphincter of the iris and the ciliary muscles. When applied topically to the eyes, it causes a rapid, intense cycloplegic and mydriatic effect that is maximal in 15 to 60 minutes; recovery usually occurs within 24 hours. The cycloplegic and mydriatic effects are slower in onset and longer in duration in patients who have dark pigmented irises.', 'DrugBank', 0, 0, 0);
