INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Aranesp 30mcg 4 pref. syringe', 14357, 'ارانسيب 30 ميكروجرام 4 سرنجات مملوءه', '4,425', '3119', 'Hematopoietic.erythropoiesis-stimulating agent (esa)', 'Hematopoietic.erythropoiesis-stimulating agent (esa)', NULL, 'Darbepoetin alfa', 'Amgen > multipharma', 'Syringe', 'حقنة', '30mcg', '4', 'علاج الانيميا الشديدة -- زيادة تكوين خلايا الدم الحمراء في حالات فشل الكلوي او مع العلاج الكيميائي للاورام', NULL, 'darbepoetin alfa is a synthetic form of erythropoietin. it stimulates erythropoiesis (increases red blood cell levels) and is used to treat anemia commonly associated with chronic renal failure and cancer chemotherapy.', NULL, 1708, '2024-11-08', NULL, 0, 'For the treatment of anemia (from renal transplants or certain HIV treatment)', 'Darbepoetin alfa stimulates erythropoiesis by the same mechanism as endogenous erythropoietin. Erythropoietin interacts with

progenitor stem cells to increase red cell production. Binding of erythropoietin to the erythropoietin receptor leads to receptor dimerization, which facilitates activation of JAK-STAT signaling pathways within the cytosol. Activated STAT (signal transducers and activators of transcription) proteins are then translocated to the nucleus where they serve as transcription factors which regulate the activation of specific genes involved in cell division or differentiation.', 'Darbepoetin alfa is used in the treatment of anemia. It is involved in the regulation of erythrocyte differentiation and the maintenance of a physiological level of circulating erythrocyte mass.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Cadex 30 tablets', 15601, 'كادكس 30 قرص', '120', '60', 'Calcium supplement', 'Calcium supplement', NULL, 'Calcium citrate +magnesium +vitamin c', 'Pharmazad > ziax', 'Tablet', 'أقراص', NULL, '3', 'مصدر للكالسيوم -- علاج نقص الكالسيوم -- الحفاظ علي صحة العظام والاسنان -- مقوي للاعصاب', NULL, NULL, NULL, 2713, '2025-01-09', NULL, 0, 'Calcium acetate is one of a number of calcium salts used to treat hyperphosphatemia (too much phosphate in the blood) in patients with kidney disease.', 'Calcium acetate and other calcium salts are phosphate binders. They work by binding with the phosphate in the food you eat, so that it is eliminated from the body without being absorbed.', 'Patients with advanced renal insufficiency (creatinine clearance less than 30 ml/min) exhibit phosphate retention and some degree of hyperphosphatemia. The retention of phosphate plays a pivotal role in causing secondary hyperparathyroidism associated with osteodystrophy, and soft-tissue calcification. The mechanism by which phosphate retention leads to hyperparathyroidism is not clearly delineated. Therapeutic efforts directed toward the control of hyperphosphatemia include reduction in the dietary intake of phosphate, inhibition of absorption of phosphate in the intestine with phosphate binders, and removal of phosphate from the body by more efficient methods of dialysis. The rate of removal of phosphate by dietary manipulation or by dialysis is insufficient. Dialysis patients absorb 40% to 80% of dietary phosphorus. Therefore, the fraction of dietary phosphate absorbed from the diet needs to be reduced by using phosphate binders in most renal failure patients on maintenance dialysis. Calcium acetate when taken with meals combines with dietary phosphate to form insoluble calcium phosphate which is excreted in the feces. Maintenance of serum phosphorus below 6.0 mg/dl is generally considered as a clinically acceptable outcome of treatment with phosphate binders. Calcium acetate is highly soluble at neutral pH, making the calcium readily available for binding to phosphate in the proximal small intestine.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Ciprofloxmet 500/500mg 20 f.c.tabs.', 32071, 'سيبروفلوكسميت 500/500مجم 20 قرص', '148', '102', 'Quinolone antibiotic with antiprotozoal', 'Quinolone antibiotic with antiprotozoal', NULL, 'Ciprofloxacin+metronidazole', 'Hp pharma', 'Tab', 'أقراص', '500mg', '2', 'مضاد حيوى واسع المدى يستخدم فى علاج التهاب المسالك البولية، والتهابات المثانة، وعدوى الجهاز التنفسي', NULL, 'anti- infective antibiotic. ciprofloxacin is a combination of ciprofloxacin 500 mg (oral fluroquinolones) and metronidazole 500 mg (for anarobic infections). it is indicated for mixed infections including intra-abdominal infections diabetic foot infection', '6224011394093', 3680, '2024-11-17', NULL, 0, NULL, NULL, NULL, NULL);
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Vitacid c plus 12 eff tablet', 12977, 'فيتاسيد سي بلس 12 قرص فوار', '35', NULL, 'Vitamin c', 'Vitamin c', NULL, 'Vitamin c+zinc', 'Cid', 'Tablet', 'أقراص', NULL, '1', NULL, NULL, NULL, '6221043013701', 2197, '2022-08-25', NULL, 0, 'For the treatment of vitamin A deficiency.', 'Vision:Vitamin A (all-<i>trans</i> retinol) is converted in the retina to the 11-<i>cis</i>-isomer of retinaldehyde or 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal functions in the retina in the transduction of light into the neural signals necessary for vision. 11-<i>cis</i>-retinal, while attached to opsin in rhodopsin is isomerized to all-<i>trans</i>-retinal by light. This is the event that triggers the nerve impulse to the brain which allows for the perception of light. All-<i>trans</i>-retinal is then released from opsin and reduced to all-<i>trans</i>-retinol. All-<i>trans</i>-retinol is isomerized to 11-<i>cis</i>-retinol in the dark, and then oxidized to 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal recombines with opsin to re-form rhodopsin. Night blindness or defective vision at low illumination results from a failure to re-synthesize 11-<i>cis</i> retinal rapidly. Epithelial differentiation: The role of Vitamin A in epithelial differentiation, as well as in other physiological processes, involves the binding of Vitamin A to two families of nuclear retinoid receptors (retinoic acid receptors, RARs; and retinoid-X receptors, RXRs). These receptors function as ligand-activated transcription factors that modulate gene transcription. When there is not enough Vitamin A to bind these receptors, natural cell differentiation and growth are interrupted.', 'Vitamin A is effective for the treatment of Vitamin A deficiency. Vitamin A refers to a group of fat-soluble substances that are structurally related to and possess the biological activity of the parent substance of the group called all-<i>trans</i> retinol or retinol. Vitamin A plays vital roles in vision, epithelial differentiation, growth, reproduction, pattern formation during embryogenesis, bone development, hematopoiesis and brain development. It is also important for the maintenance of the proper functioning of the immune system.', 'DrugBank');
