INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (24505, 'Pansoral oral gel 15 gm', 'بانسورال جل للفم 15جم', '6', NULL, 'Oral care', 'Cetalkonium chloride+choline salicylate', 'Global napi pharmaceuticals > pierre - fabre france', 'Gel', 'جل', '15 gm', '1', NULL, 'about cetalkonium cationic quaternary ammonium surfactant anti-infective and disinfectant. indications for cetalkonium topical anti infective and disinfectant.', NULL, NULL, 838, '2022-08-01', 0, 'Cetalkonium is used as a surfactant, germicide, fungicide, mildew preventive agents in silicone-based water repellents, emulsifier, disinfectant, topical anti-infective, and antiseptic antimicrobial for pharmaceuticals. For its chemical properties and its antiseptical characteristics, cetalkonium can be used in a wide variety of products as part of the formulations and it is also determined to be an active antiseptic ingredient in over-the-counter preparations.[T211]', 'The activity of cetalkonium is based on the creation of a positive charge which permits a bio-adhesive property to negative surfaces.[L2809] This property allows cetalkonium to disrupt the cell membrane, inactivate enzymes and denature proteins. This type of mechanism allows this type of agents to present a broad spectrum.[T212]', 'Cetalkonium is an antimicrobial agent and it has shown to be effective against both gram-positive, gram-negative bacteria and fungi. From this property, it has been shown to present a better activity against the gram-negative organisms.[F92]', 'DrugBank', 0, 0, 0);
