INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (24693, 'Pentaviton 30 f.c. tab.', 'بنتافيتون 30 قرص', '27', NULL, 'Multivitamins', 'Folic acid+vitamin b1+vitamin b2+vitamin b6+vitamin b12', 'International drug agency (idi) > novell pharma', 'Tab', 'أقراص', NULL, '3', 'مكمل غذائى لعلاج الانيميا ودعم الجهاز العصبى وامداد الجسم بالطاقة', NULL, '6223004070525', NULL, 1746, '2022-12-26', 0, 'Folic acid is indicated for the treatment of folic acid deficiency, megaloblastic anemia, and in anemias of nutritional origins, pregnancy, infancy, or childhood.', 'Folic acid, as it is biochemically inactive, is converted to tetrahydrofolic acid and methyltetrahydrofolate by dihydrofolate reductase (DHFR). These folic acid congeners are transported across cells by receptor-mediated endocytosis where they are needed to maintain normal erythropoiesis, synthesize purine and thymidylate nucleic acids, interconvert amino acids, methylate tRNA, and generate and use formate. Using vitamin B12 as a cofactor, folic acid can normalize high homocysteine levels by remethylation of homocysteine to methionine via methionine synthetase.', 'Folic acid is a water-soluble B-complex vitamin found in foods such as liver, kidney, yeast, and leafy, green vegetables. Also known as folate or Vitamin B9, folic acid is an essential cofactor for enzymes involved in DNA and RNA synthesis. More specifically, folic acid is required by the body for the synthesis of purines, pyrimidines, and methionine before incorporation into DNA or protein. Folic acid is the precursor of tetrahydrofolic acid, which is involved as a cofactor for transformylation reactions in the biosynthesis of purines and thymidylates of nucleic acids. Impairment of thymidylate synthesis in patients with folic acid deficiency is thought to account for the defective deoxyribonucleic acid (DNA) synthesis that leads to megaloblast formation and megaloblastic and macrocytic anemias. Folic acid is particularly important during phases of rapid cell division, such as infancy, pregnancy, and erythropoiesis, and plays a protective factor in the development of cancer. As humans are unable to synthesize folic acid endogenously, diet and supplementation is necessary to prevent deficiencies. In order to function properly within the body, folic acid must first be reduced by the enzyme dihydrofolate reductase (DHFR) into the cofactors dihydrofolate (DHF) and tetrahydrofolate (THF). This important pathway, which is required for de novo synthesis of nucleic acids and amino acids, is disrupted by anti-metabolite therapies such as [DB00563] as they function as DHFR inhibitors to prevent DNA synthesis in rapidly dividing cells, and therefore prevent the formation of DHF and THF. 



In general, folate serum levels below 5 ng/mL indicate folate deficiency, and levels below 2 ng/mL usually result in megaloblastic anemia.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (24694, 'Pentaxim vaccine 0.5ml', 'بينتاكسيم فاكسين لقاح 0.5مل', '126', NULL, 'Vaccine', 'Diphtheria+haemophilus influenza b+pertussis+poliomyelitis+tetanus toxoid', 'Sanofi pasteur-france > etimex-egypt', 'Pen', 'قلم', '0.5ml', '1', NULL, NULL, NULL, NULL, 1142, '2022-07-14', 0, NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (24695, 'Pentolate 1% eye drops 10 ml', 'بنتولات قطرة عين 10 مل', '8', NULL, 'Mydriatic', 'Cyclopentolate', 'Amoun', 'Pen', 'قلم', '1%', '1', NULL, 'about cyclopentolate ophthalmic anticholinergic(anti-muscarinic) a mydriatic and cycloplegic agent for diagnostic purposes. mechanism of action of cyclopentolate cyclopentolate is a muscarinic receptor blocker. it nonselectively inhibits the muscarinic ac', '6221025017000', NULL, 1043, '2023-04-18', 0, 'Used mainly to produce mydriasis and cycloplegia for diagnostic purposes.', 'By blocking muscarinic receptors, cyclopentolate produces dilatation of the pupil (mydriasis) and prevents the eye from accommodating for near vision (cycloplegia).', 'Cyclopentolate is an anti-muscarinic in the same class as atropine and scopolamine. Cyclopentolate blocks the receptors in the muscles of the eye (muscarinic receptors). These receptors are involved controlling the pupil size and the shape of the lens. Cyclopentolate thus induces relaxation of the sphincter of the iris and the ciliary muscles. When applied topically to the eyes, it causes a rapid, intense cycloplegic and mydriatic effect that is maximal in 15 to 60 minutes; recovery usually occurs within 24 hours. The cycloplegic and mydriatic effects are slower in onset and longer in duration in patients who have dark pigmented irises.', 'DrugBank', 0, 0, 0);
