INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (24989, 'Plasbumin 20% i.v.infusion 50ml', 'بلازبومين 20% محلول للحقن الوريدي', '1,450', '962', 'Human albumin', 'Human albumin', 'Talecris biotherapeutics-usa > biolinx medical inc.', 'Unknown', 'غير محدد', '20%', '1', NULL, NULL, NULL, NULL, 1153, '2025-08-23', 0, 'Human immunoglobulin G is indicated for the following conditions:



### Primary Immunodeficiency

- for the treatment of primary immunodeficiency in adult and pediatric patients[L39920, L39930, L39935, L39940, L39945, L39950, L39955, L39960, L39965, L39975, L39980, L40029, L40034, L40044]

- in combination with [hyaluronidase (human recombinant)] for the treatment of primary immunodeficiency in patients ≥2 years of age.[L42760]



