    ) VALUES (4122, 'Emerest 8mg/4ml 3 ampoules', 'ايميرست 8مجم/4 مل 3 امبولات', '159', '144', 'Antiemetic', 'Ondansetron', 'Global pharmaceutical industries', 'Ampoule', 'أمبول', '8mg', '3', 'مضاد للقيء - للترجيع', 'In the adult patient population:

