    ) VALUES (9761, 'Biobelle 120 ml hair lotion', 'بيوبيل لوشن للشعر 120 مل', '150', '135', 'Hair lotion', 'Caffeine + biotin + saw palmetto extract + ginko biloba root extract + salicylic acid + panthenol + coconut oil + linalool', 'Egy biotics', 'Lotion', 'لوشن', '120 ml', '1', NULL, 'Caffeine is indicated for the short term treatment of apnea of prematurity in infants and off label for the prevention and treatment of bronchopulmonary dysplasia caused by premature birth.[T716,L9851] In addition, it is indicated in combination with sodium benzoate to treat respiratory depression resulting from an overdose with CNS depressant drugs.[L9899] Caffeine has a broad range of over the counter uses, and is found in energy supplements, athletic enhancement products, pain relief products, as well as cosmetic products.[T716,L9854,L9872]', NULL, NULL, 909, '2025-06-17', 0, 0, 0);
