    ) VALUES (18306, 'Endometrin 100mg 21 vaginal tablets', 'اندوميترين 100مجم 21 قرص مهبلي', '280', NULL, 'Progesterone supplement', 'Progesterone', 'Ferring pharmaceuticals > multipharma', 'Tablet', 'أقراص', '100mg', '3', NULL, '**Gelatinized capsules**



