    ) VALUES (2270, 'Cefozon 1 gm vial', 'سيفوزون 1جرام 1فيال', '48', '32', 'Antibiotic.cephalosporin.third-generation', 'Cefoperazone', 'Eipico', 'Vial', 'فيال', '1 gm', '1', NULL, 'Indicated for the treatment of following infections caused by susceptible bacteria:[label]



