INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (14041, 'Ampicillin-el nasr 250mg 12 caps.', 'امبيسيلين النصر 250 مجم 12 كبسولة', '7', NULL, 'Penicillins.monocomponent', 'Ampicillin', 'El nasr', 'Cap', 'كبسولة', '250mg', '2', NULL, 'description : ssemi-synthetic derivative of penicillin that functions as an orally active broad-spectrum antibiotic .ssindication :sfor treatment of infection (respiratory gi uti and meningitis) due to e. coli p. mirabilis enterococci shigella s. typhosa', NULL, NULL, 832, '2022-07-25', 'For treatment of infection (Respiratory, GI, UTI and meningitis) due to E. coli, P. mirabilis, enterococci, Shigella, S. typhosa and other Salmonella, nonpenicillinase-producing N. gononhoeae, H. influenzae, staphylococci, streptococci including streptoc', 'By binding to specific penicillin-binding proteins (PBPs) located inside the bacterial cell wall, Ampicillin inhibits the third and last stage of bacterial cell wall synthesis. Cell lysis is then mediated by bacterial cell wall autolytic enzymes such as autolysins; it is possible that Ampicillin interferes with an autolysin inhibitor.', 'Ampicillin is a penicillin beta-lactam antibiotic used in the treatment of bacterial infections caused by susceptible, usually gram-positive, organisms. The name "penicillin" can either refer to several variants of penicillin available, or to the group of antibiotics derived from the penicillins. Ampicillin has <i>in vitro</i> activity against gram-positive and gram-negative aerobic and anaerobic bacteria. The bactericidal activity of Ampicillin results from the inhibition of cell wall synthesis and is mediated through Ampicillin binding to penicillin binding proteins (PBPs). Ampicillin is stable against hydrolysis by a variety of beta-lactamases, including penicillinases, and cephalosporinases and extended spectrum beta-lactamases.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (18794, 'Farabicure 1% top. cream 15 gm', 'فارابيكور 1% كريم 15 جم', '22', NULL, 'Antifungals.allylamines', 'Butenafine hydrochloride', 'Egpi > farabi', 'Cream', 'كريم', '1%', '1', NULL, NULL, NULL, NULL, 916, '2022-07-11', 'For the topical treatment of the following dermatologic infections: tinea (pityriasis) versicolor due to <i>M. furfur</i>, interdigital tinea pedis (athlete&rsquo;s foot), tinea corporis (ringworm) and tinea cruris (jock itch) due to <i>E. floccosum</i>, <i>T. mentagrophytes</i>, <i>T. rubrum</i>, and <i>T. tonsurans</i>.', 'Although the  mechanism of action has not been fully established, it has been suggested that butenafine, like allylamines, interferes with sterol biosynthesis (especially ergosterol) by inhibiting squalene monooxygenase, an enzyme responsible for converting squalene to 2,3-oxydo squalene. As ergosterol is an essential component of the fungal cell membrane, inhibition of its synthesis results in increased cellular permeability causing leakage of cellular contents. Blockage of squalene monooxygenase also leads to a subsequent accumulation of squalene. When a high concentration of squalene is reached, it is thought to have an effect of directly kill fungal cells.', 'Butenafine is a synthetic antifungal agent that is structurally and pharmacologically related to allylamine antifungals. The exact mechanism of action has not been established, but it is suggested that butenafine''s antifungal activity is exerted through the alteration of cellular membranes, which results in increased membrane permeability, and growth inhibition. Butenafine is mainly active against dermatophytes and has superior fungicidal activity against this group of fungi when compared to that of terbinafine, naftifine, tolnaftate, clotrimazole, and bifonazole. It is also active against <i>Candida albicans</i> and this activity is superior to that of terbinafine and naftifine. Butenafine also generates low MICs for <i>Cryptococcus neoformans</i> and <i>Aspergillus spp.</i> as well.', 'DrugBank', 0, 0, 0);
