INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (6116, 'Hi-dac 20 tab.', 'هاي داك 20قرص', '17', NULL, 'Nsaid.acetic acid derivatives', 'Sulindac', 'Hi-pharm', 'Tab', 'أقراص', NULL, '2', 'مسكن عام -- تقليل الالام بالجسم -- تقليل الالتهابات', NULL, '6224001050190', NULL, 851, '2022-12-05', 'For acute or long-term use in the relief of signs and symptoms of osteoarthritis, rheumatoid arthritis, ankylosing spondylitis, acute painful shoulder (acute subacromial bursitis/supraspinatus tendinitis), and acute gouty arthritis.', 'Sulindac''s exact mechanism of action is unknown. Its antiinflammatory effects are believed to be due to inhibition of both COX-1 and COX-2 which leads to the inhibition of prostaglandin synthesis. Antipyretic effects may be due to action on the hypothalamus, resulting in an increased peripheral blood flow, vasodilation, and subsequent heat dissipation.', 'Sulindac is a non-steroidal anti-inflammatory indene derivative, also possessing analgesic and antipyretic activities.', 'DrugBank', 0, 0, 0);
