INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (25043, 'Polymer baby isotonic 0.9% nasal spray 100ml', 'بوليمير بيبي ايزوتونيك 0.9% بخاخ انف 100 مل', '300', '275', 'Nasal decongestant', 'Natural purified and isotonic seawater', 'Uk medica limited > glory medical group', 'Spray', 'بخاخ', '0.9%', '1', 'مزيل لاحتقان الانف ومدعم للجهاز التنفسي', '*it s pediatric 0.9% isotonic. *it s for effective nasal hygiene and removal of mucosity in young children.', '735850480008', NULL, 8166, '2025-08-03', 0, NULL, 'The predominant targets of NK cells are ‘stressed’ cells with downregulated expression of class I MHC (MHC-I), a common mechanism used by malignant or virus-infected cells to evade recognition by the body’s immune system. In response to encountering these cells, NK cells release two types of proteins, perforins and granzyme B, which attack the target cell’s membrane, damage its organelles, and trigger apoptosis. NK Cell immunotherapy is an emerging anti-cancer strategy, as these cells are able to exert effects without activating the graft-versus-host disease cascade. In addition, their broad mechanism allows for generally safe administration of NK cells that are not immunologically matched to a patient. ', NULL, 'DrugBank', 0, 0, 0);
