INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (25111, 'Powerecta 10mg 4 disinteg. tabs.', 'باوريكتا 10مجم 4 قرص', '30', NULL, 'Tonic for men', 'Vardenafil', 'Eva pharma', 'Tab', 'أقراص', '10mg', '4', NULL, NULL, '6222006500900', NULL, 2114, '2022-10-28', 0, 'Vardenafil is indicated for the treatment of erectile dysfunction.[L45563,L45568,L45573]', 'Vardenafil inhibits cyclic guanosine monophosphate (GMP) specific phosphodiesterase type 5 (PDE5), which is responsible for the degradation of cyclic GMP in the corpus cavernosum located around the penis. Penile erection during sexual stimulation is caused by increased penile blood flow resulting from the relaxation of penile arteries and corpus cavernosal smooth muscle. This response is mediated by the release of nitric oxide (NO) from nerve terminals and endothelial cells, which stimulates the synthesis of cyclic GMP in smooth muscle cells. Cyclic GMP causes smooth muscle relaxation and increased blood flow into the corpus cavernosum. The tissue concentration of cyclic GMP is regulated by both the rates of synthesis and degradation via phosphodiesterases (PDEs), and the most abundant PDE in the human corpus cavernosum is PDE5. Therefore, the inhibition of PDE5 by vardenafil enhances erectile function by increasing the amount of cyclic GMP.[L45563]', 'Vardenafil is a potent and selective inhibitor of cyclic guanosine monophosphate (cGMP)-specific phosphodiesterase type 5 (PDE5), an enzyme responsible for the degradation of cGMP in the corpus cavernosum. The presence of cGMP in the corpus cavernosum leads to smooth muscle relaxation, an increased inflow of blood and an erection. Therefore, 