INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (25283, 'Probactcin 2% top. cream 10 gm', 'بروباكتسين2%كريم موضعي 10جم', '37', NULL, 'Anti-bacterial.protein synthesis inhibitor', 'Mupirocin', 'Kahira > october pharma', 'Cream', 'كريم', '2%', '1', NULL, 'about mupirocin a naturally antibiotic isolated from pseudomonas fluorescens antibiotic.ssmechanism of action of mupirocinsmupirocin exerts its action by reversibly binding to isoleucyl transfer rna synthetase and inhibits the enzyme activity. this enzyme', NULL, NULL, 891, '2022-07-07', 0, 'Indicated for the treatment of impetigo and secondary skin infections, leading to traumatic skin lesions, due to _Staphylococcus aureus_ and _Streptococcus pyogenes_.[L10580] ', 'Mupirocin specifically and reversibly binds to bacterial isoleucyl transfer-RNA (tRNA) synthetase, which is an enzyme that promotes the conversion of isoleucine and tRNA to isoleucyl-tRNA. Inhibition of this enzyme subsequently leads to the inhibition of the bacterial protein and RNA synthesis.[A178531] Mupirocin is bacteriostatic at lower concentrations but it exerts bactericidal effects with prolonged exposure, killing 90-99% of susceptible bacteria over a 24 hour period.[A178552]', 'Mupirocin is reported to be active against susceptible aerobic gram-positive cocci, such as _Staphylococcus aureus_, _Staphylococcus epidermidis_, and other beta-hemolytic streptococci_Streptococcus pyogenes_.[A178594] It mediates its antibacterial activity by inhibiting the bacterial protein synthesis and formation of bacterial proteins essential for survival. The minimum bactericidal concentration (MBC) against relevant pathogens is generally eight-fold to thirty-fold higher than the minimum inhibitory concentration (MIC).[L10580] In one clinical study investigating the therapeutic effectiveness of topical mupirocin in impetigo, the therapeutic response rate was about 94 to 98% after one week following the end of therapy.[L10580] In clinical studies of patients with primary and secondary skin infections, both elimination of the bacterial pathogen and clinical cure or improvement hav been demonstrated in over 90% of patients receiving topical mupirocin.[A178591] Mupirocin resistance as high as 81% has been reported previously.[A178600] Resistance to mupirocin, which occurs more frequently in methicillin-resistant than methicillin-susceptible staphylococci, may occur with the production of a modified isoleucyl-tRNA synthetase, or the acquisition of, by genetic transfer, a plasmid mediating a new isoleucyl-tRNA synthetase.[L10580]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (25284, 'Probiontal 5 mg 10 tab.', 'بروببيونتال 5مجم 10 اقراص', '3', NULL, 'Biotin supplement', 'Biotin', 'Hikma pharma', 'Tab', 'أقراص', '5 mg', '1', NULL, 'mechanism of action of biotin biotin excerts it`s pharmacological action by influencing metabolic reactions. it acts as a coenzyme for carbohydrate fat and protein metabolism. it is required for cell growth and production of fatty acids. it is required fo', NULL, NULL, 1286, '2022-07-13', 0, 'For nutritional supplementation, also for treating dietary shortage or imbalance.', 'Biotin is necessary for the proper functioning of enzymes that transport carboxyl units and fix carbon dioxide, and is required for various metabolic functions, including gluconeogenesis, lipogenesis, fatty acid biosynthesis, propionate metabolism, and catabolism of branched-chain amino acids.', 'Biotin is a water-soluble B-complex vitamin which is composed of an ureido ring fused with a tetrahydrothiophene ring, which attaches a valeric acid substituent at one of its carbon atoms. Biotin is used in cell growth, the production of fatty acids, metabolism of fats, and amino acids. It plays a role in the Kreb cycle, which is the process in which energy is released from food. Biotin not only assists in various metabolic chemical conversions, but also helps with the transfer of carbon dioxide. Biotin is also helpful in maintaining a steady blood sugar level. Biotin is often recommended for strengthening hair and nails. Consequenty, it is found in many cosmetic and health products for the hair and skin. Biotin deficiency is a rare nutritional disorder caused by a deficiency of biotin. Initial symptoms of biotin deficiency include: Dry skin, Seborrheic dermatitis, Fungal infections, rashes including erythematous periorofacial macular rash, fine and brittle hair, and hair loss or total alopecia. If left untreated, neurological symptoms can develop, including mild depression, which may progress to profound lassitude and, eventually, to somnolence; changes in mental status, generalized muscular pains (myalgias), hyperesthesias and paresthesias. The treatment for biotin deficiency is to simply start taking some biotin supplements. A lack of biotin in infants will lead to a condition called seborrheic dermatitis or "cradle cap". Biotin deficiencies are extremely rare in adults but if it does occur, it will lead to anemia, depression, hair loss, high blood sugar levels, muscle pain, nausea, loss of appetite and inflamed mucous membranes.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (25285, 'Probric 10 mg 10 tab.', 'بروبريك 10مجم 10 اقراص', '17', NULL, 'Bronchodilator.long acting beta 2 agonist', 'Bambuterol', 'Borg', 'Tab', 'أقراص', '10 mg', '1', NULL, 'about bambuterol long acting beta 2-adrenoceptor agonist anti asthmatic-bronchodilators. mechanism of action of bambuterol this prodrug of the 2-agonist terbutaline undergoes oxidation and hydrolysis in the body and converted into terbutaline. this terbut', '6223002570737', NULL, 1029, '2022-07-27', 0, 'For the prevention and reversal of bronchospasm in patients 12 years of age and older with asthma and reversible bronchospasm associated with bronchitis and emphysema.', 'The pharmacologic effects of bambuterol are at least in part attributable to stimulation through beta-adrenergic receptors (beta 2 receptors) of intracellular adenyl cyclase, the enzyme that catalyzes the conversion of adenosine triphosphate (ATP) to cyclic AMP. Increased cyclic AMP levels are associated with relaxation of bronchial smooth muscle and inhibition of release of mediators of immediate hypersensitivity from cells, especially from mast cells.', 'Bambuterol is a long acting beta2-adrenoceptor agonist used in the treatment of asthma. It is a prodrug of terbutaline. Bambuterol causes smooth muscle relaxation, resulting in dilation of bronchial passages.', 'DrugBank', 0, 0, 0);
