INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (25413, 'Protectazole 600 mg 5 vag. tabs.', 'بروتيكتازول 600مجم 5 اقراص', '56', NULL, 'Antifungals.imidazoles', 'Oxiconazole', 'Egpi > jasper for pharmaceutical industries', 'Tab', 'أقراص', '600 mg', '1', NULL, 'about oxiconazole imidazole derivative antifungal. mechanism of action of oxiconazole oxiconazole is fungicidal or fungistatic depending on concentrations. it inhibits the conversion of lanosterol to 14 demethyl lanosterol by inhibiting the cytochromep450', NULL, NULL, 843, '2022-07-26', 0, 'For treatment of dermal fungal infection.', 'Oxiconazole inhibits ergosterol biosynthesis, which is required for cytoplasmic membrane integrity of fungi. It acts to destabilize the fungal cyctochrome P450 51 enzyme (also known as Lanosterol 14-alpha demethylase). This is vital in the cell membrance structure of the fungus. Its inhibition leads to cell lysis. Oxiconazole has also been shown in inhibit DNA synthesis and suppress intracellular concentrations of ATP. Like other imidazole antifungals, Oxiconazole can increase membrane permeability to zinc, augmenting its cytotoxicity.', 'Oxiconazole is a broad-spectrum imidazole derivative whose antifungal activity is derived primarily from the inhibition of ergosterol biosynthesis, which is critical for cellular membrane integrity. It has fungicidal or fungistatic activity in vitro against a number of pathogenic fungi including the following dermatophytes, and yeasts: <i>T. rubrum</i>, <i>T. mentagrophytes</i>, <i>T. tonsurans</i>, <i>T. violaceum</i>, <i>E. floccosum</i>, <i>M. canis</i>, <i>M. audouini</i>, <i>M. gypseum</i>, <i>C. albicans</i>, and <i>M. furfur</i>.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (25414, 'Protectopril 8 mg 20 tabs.', 'بروتيكتوبريل 8مجم 20 اقراص', '44', NULL, 'Antihypertensive.ace', 'Perindopril', 'Andalous pharma', 'Tab', 'أقراص', '8 mg', '2', NULL, 'about perindopril a long-acting dicarboxylate-containing ace inhibitor antihypertensive. mechanism of action of perindopril it is a prodrug and converted in to active perindoprilat; which is a long acting angiotensin converting enzyme inhibitor prevents t', '6223004692628', NULL, 776, '2022-07-15', 0, 'For the treatment of mild to moderate essential hypertension, mild to moderate congestive heart failure, and to reduce the cardiovascular risk of individuals with hypertension or post-myocardial infarction and stable coronary disease. ', 'There are two isoforms of ACE: the somatic isoform, which exists as a glycoprotein comprised of a single polypeptide chain of 1277; and the testicular isoform, which has a lower molecular mass and is thought to play a role in sperm maturation and binding of sperm to the oviduct epithelium. Somatic ACE has two functionally active domains, N and C, which arise from tandem gene duplication. Although the two domains have high sequence similarity, they play distinct physiological roles. The C-domain is predominantly involved in blood pressure regulation while the N-domain plays a role in hematopoietic stem cell differentiation and proliferation. ACE inhibitors bind to and inhibit the activity of both domains, but have much greater affinity for and inhibitory activity against the C-domain. Perindoprilat, the active metabolite of perindopril, competes with ATI for binding to ACE and inhibits and enzymatic proteolysis of ATI to ATII. Decreasing ATII levels in the body decreases blood pressure by inhibiting the pressor effects of ATII as described in the Pharmacology section above. Perindopril also causes an increase in plasma renin activity likely due to a loss of feedback inhibition mediated by ATII on the release of renin and/or stimulation of reflex mechanisms via baroreceptors. ', 'Perindopril is a nonsulfhydryl prodrug that is metabolized via first pass effect (62%) and systemic hydrolysis (38%) to perindoprilat, its active metabolite, following oral administration. Perindoprilat lowers blood pressure by antagonizing the effect of the RAAS. The RAAS is a homeostatic mechanism for regulating hemodynamics, water and electrolyte balance. During sympathetic stimulation or when renal blood pressure or blood flow is reduced, renin is released from the granular cells of the juxtaglomerular apparatus in the kidneys. In the blood stream, renin cleaves circulating angiotensinogen to ATI, which is subsequently cleaved to ATII by ACE. ATII increases blood pressure using a number of mechanisms. First, it stimulates the secretion of aldosterone from the adrenal cortex. Aldosterone travels to the distal convoluted tubule (DCT) and collecting tubule of nephrons where it increases sodium and water reabsorption by increasing the number of sodium channels and sodium-potassium ATPases on cell membranes. Second, ATII stimulates the secretion of vasopressin (also known as antidiuretic hormone or ADH) from the posterior pituitary gland. ADH stimulates further water reabsorption from the kidneys via insertion of aquaporin-2 channels on the apical surface of cells of the DCT and collecting tubules. Third, ATII increases blood pressure through direct arterial vasoconstriction. Stimulation of the Type 1 ATII receptor on vascular smooth muscle cells leads to a cascade of events resulting in myocyte contraction and vasoconstriction. In addition to these major effects, ATII induces the thirst response via stimulation of hypothalamic neurons. ACE inhibitors inhibit the rapid conversion of ATI to ATII and antagonize RAAS-induced increases in blood pressure. ACE (also known as kininase II) is also involved in the enzymatic deactivation of bradykinin, a vasodilator. Inhibiting the deactivation of bradykinin increases bradykinin levels and may sustain the effects of perindoprilat by causing increased vasodilation and decreased blood pressure.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (25415, 'Protectopril plus 4/1.25 mg 20 tabs.', 'بروتيكتوبريل بلس 4/1.25مجم 20 قرص', '69', '32', 'Antihypertensive.ace', 'Indapamide+perindopril', 'Andalous pharma', 'Tab', 'أقراص', '1.25 mg', '2', NULL, 'this product is used to treat high blood pressure. lowering high blood pressure helps prevent strokes heart attacks and kidney problems. this product contains 2 medications: perindopril and indapamide. perindopril belongs to a class of drugs known as ace', NULL, NULL, 1076, '2025-09-24', 0, NULL, NULL, NULL, NULL, 0, 0, 0);
