INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (25549, 'Quick-slim 120mg 30 caps.', 'كويك-سليم 120مجم 30 كبسولة', '240', '136.5', 'Weight loss', 'Orlistat', 'Marcyrl co.', 'Cap', 'كبسولة', '120mg', '3', NULL, NULL, '6223003570118', NULL, 4845, '2024-11-16', 0, 'Orlistat is indicated for obesity management including weight loss and weight maintenance when used in combination with calorie reduction in overweight and obese adults; this indication applies to both the prescription formulation of 120 mg[L11130] and the over-the-counter formulation of 60 mg.[L31963] Orlistat in the 120 mg prescription formulation is also indicated to reduce the risk of weight regain following weight loss.[L11130]', 'Orlistat is a potent and selective inhibitor of various lipase enzymes responsible for the metabolism of fat. It acts in the gastrointestinal (GI) tract via covalent binding to the serine residues located on the active site of both gastric and pancreatic lipase. When orlistat is taken with food containing fat, it partially inhibits the hydrolysis of triglycerides. This decreases absorption of monoaclglycerides and free fatty acids, contributing to weight maintenance and weight loss.[A37653,L11130]', 'Orlistat helps with weight reduction and maintenance by inhibiting the absorption of dietary fats via the inhibition of lipase enzymes.[A1734,A1735]', 'DrugBank', 0, 0, 0);
