INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (22931, 'Monopril 20mg 10 tab.', 'مونوبريل 20مجم 10 اقراص', '29', NULL, 'Antihypertensive.ace', 'Fosinopril', 'Smithkline beecham', 'Tab', 'أقراص', '20mg', '1', NULL, 'about fosinopril phosphonate-containing angiotensin-converting enzyme (ace) inhibitor antihypertensive. mechanism of action of fosinopril it is a prodrug and converted in to active fosinoprilat; which is an angiotensin converting enzyme inhibitor prevents', '6224001007347', NULL, 875, '2022-12-06', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Fosinopril is a phosphinic acid-containing ester prodrug that belongs to the angiotensin-converting enzyme (ACE) inhibitor class of medications. It is rapidly hydrolyzed to fosinoprilat, its principle active metabolite. Fosinoprilat inhibits ACE, the enzyme responsible for the conversion of angiotensin I (ATI) to angiotensin II (ATII). ATII regulates blood pressure and is a key component of the renin-angiotensin-aldosterone system (RAAS). Fosinopril may be used to treat mild to moderate hypertension, as an adjunct in the treatment of congestive heart failure, and to slow the rate of progression of renal disease in hypertensive individuals with diabetes mellitus and microalbuminuria or overt nephropathy.', '["C09BA09", "C09AA09"]', '[]');
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (22932, 'Monoshield 0.5% eye drops 10*0.5 ml sdu', 'مونوشيلد 0.5% قطرة عين 10 وحدات', '52', '36', 'Antibiotic.quinolone', 'Levofloxacin', 'Global advanced pharmaceuticals (gap) > monopharma pharmaceuticals', 'Drops', 'نقط', '0.5%', '1', 'قطرة عين مضاد حيوي', 'about levofloxacin third generation fluoroquinolone (or quinolone) a broad spectrum antiinfective. mechanism of action of levofloxacin levofloxacin is a broad spectrum antibacterial agent. it act by inhibiting the enzyme dna gyrase (topoisomerase 2) and t', NULL, NULL, 1699, '2025-08-18', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Levofloxacin is a fluoroquinolone antibiotic and the optical S-(-) isomer of racemic [ofloxacin].[A190663] It reportedly carries 8 to 128-fold more activity against both gram-negative and gram-positive bacteria compared to R-(+)-ofloxacin[A190663] and remains stereochemically stable following administration (i.e. it does not invert to the inactive isomer).[L11638] Levofloxacin, along with other quinolones such as [gatifloxacin] and [moxifloxacin], is a member of the third generation of fluoroquinolones, colloquially referred to as the "respiratory quinolones" due to improved activity against gram-positive bacteria commonly implicated in respiratory infections.[A31453,A190756]

Levofloxacin was first approved by the FDA in 1996, and was approved in Canada and several South American countries soon after.[A190663]', '["J01MA12", "A02BD10", "J01RA05", "S01AE05"]', '[]');
