INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (22953, 'Monuril 3gm 1 sachet', 'مونيوريل 3جم 1 كيس', '293', '205', 'Uti infections', 'Fosfomycin', 'Zambon > pharmacon', 'Sachet', 'أكياس', '3gm', '1', NULL, 'used for acute & recurrent cystitis in woman ï-uncomplicated urinary tract infectionï- and cystitis during pregnancy perioperative prophylaxis of utis. always mix with 90 to 120 ml cool water before ingesting; do not administer in its dry form or mix with', '52477551498113', '01080316090010081727022810397062 2152477551498113', 9086, '2025-02-06', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Fosfomycin was discovered in 1969 by scientists at the Spanish Penicillin and Antibiotics Company and is produced by _Streptomyces fradiae_.[A229338,A230348] It may also be produced synthetically and is commercially available as the disodium salt for intravenous administration and as the calcium or trometamol salt for oral administration.[A230348] In terms of chemical structure, fosfomycin is a phosphoenolpyruvate analog and contains a phosphonic group and an epoxide ring.[A230348]

Due to its ease of administration as a single 3-gram oral dose and desirable safety profile, fosfomycin has largely become a first-line therapeutic option for the treatment of uncomplicated urinary tract infections (UTIs) in females.[A230378] Despite being FDA approved only for urinary tract infections, fosfomycin actually has a broad spectrum of activity and is active against both gram-positive and gram-negative bacteria.[A230348] As such there is great interest in exploring the usefulness of fosfomycin for indications beyond the treatment of UTIs.[A230353,A230358]', '["J01XX01"]', '[]');
