INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (25845, 'Relpax 40mg 3 f.c. tab.', 'ريلباكس 40مجم 3 قرص', '72', NULL, 'Antimigraine', 'Eletriptan', 'Pfizer', 'Tab', 'أقراص', '40mg', '1', NULL, 'about eletriptan selective 5 ht 1b/1d agonist antimigraine agent mechanism of action of eletriptan eletriptan is a selective 5-hydroxytryptamine (serotonin5-ht) type 1b and 1d (5-ht1b/1d) receptor agonist. it is thought to be related to activation of 5-ht', NULL, NULL, 1673, '2022-07-12', 0, 'For the acute treatment of migraine with or without aura in adults.', 'Eletriptan binds with high affinity to 5-HT1B, 5-HT1D and 5-HT1F receptors, has modest affinity for 5-HT1A, 5-HT1E, 5-HT2B and 5-HT7 receptors, and little or no affinity for 5-HT2A, 5-HT2C, 5-HT3, 5-HT4, 5-HT5A and 5-HT6 receptors. In contrast, eletriptan displays insignificant pharmacological activity at adrenergic alpha1, alpha2, or beta; dopaminergic D1 or D2; muscarinic; or opioid receptors. While the full mechanism of action of 5-HT receptor agonists in relieving migrains is not fully elucidated, it is proposed that the activation of 5-HT1 receptors located on intracranial blood vessels leads to vasoconstriction that correlates with the relief of migraine headaches. It is also proposed that the activation of 5-HT1 receptors on sensory nerve endings in the trigeminal system leads to the inhibition of release of pro-inflammatory neuropeptides. ', 'Eletriptan is a selective 5-hydroxytryptamine 1B/1D receptor agonist. In the anesthetized dog, eletriptan has been shown to reduce carotid arterial blood flow, with only a small increase in arterial blood pressure at high doses. While the effect on blood flow was selective for the carotid arterial bed, decreases in coronary artery diameter were observed. Eletriptan has also been shown to inhibit trigeminal nerve activity in the rat.', 'DrugBank', 0, 0, 0);
