INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (1264, 'Bacticlor 375mg 10 cap', 'بكتيكلور 375مجم 10 كبسولات', '30', NULL, 'Antibiotic.cephalosporin.second-generation', 'Cefaclor', 'Pharco b > ranbaxy', 'Cap', 'كبسولة', '375mg', '1', NULL, NULL, NULL, NULL, 952, '2023-04-19', 'For the treatment of certain infections caused by bacteria such as pneumonia and ear, lung, skin, throat, and urinary tract infections.', 'Cefaclor, like the penicillins, is a beta-lactam antibiotic. By binding to specific penicillin-binding proteins (PBPs) located inside the bacterial cell wall, it inhibits the third and last stage of bacterial cell wall synthesis. Cell lysis is then mediated by bacterial cell wall autolytic enzymes such as autolysins. It is possible that cefaclor interferes with an autolysin inhibitor.', 'Cefaclor is a second generation cephalosporin antibiotic with a spectrum resembling first-generation cephalosporins. <i>In vitro</i> tests demonstrate that the bactericidal action of the cephalosporins results from inhibition of cell-wall synthesis. As indicated by _in vitro_ and _in vivo_ clinical studies, cefaclor was shown to be effective against most strains of Gram positive aerobes - Staphylococci (including coagulase-positive, coagulase-negative, and penicillinase-producing strains), <i>Streptococcus pneumoniae</i>, <i>Streptococcus pyogenes</i> (group A &szlig;-hemolytic streptococci), as well as Gram-negative aerobes - <i>Escherichia coli</i>, <i>Haemophilus influenzae</i> (including &szlig;-lactamase-producing ampicillin-resistant strains), <i>Klebsiella sp</i>, and <i>Proteus mirabilis</i>.', 'DrugBank', 0, 0, 0);
