INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (26429, 'Sabfrinate 2.5 mg 2 f.c. tabs.', 'سابفرينيت 2.5مجم 2 قرص', '14', NULL, NULL, 'Frovatriptan', 'Sab trading', 'Tab', 'أقراص', '2.5 mg', '1', NULL, NULL, NULL, NULL, 491, '2022-08-05', 0, 'For the acute treatment of migraine attacks with or without aura in adults.', 'Three distinct pharmacological actions have been implicated in the antimigraine effect of the triptans: (1) stimulation of presynaptic 5-HT<sub>1D</sub> receptors, which serves to inhibit both dural vasodilation and inflammation; (2) direct inhibition of trigeminal nuclei cell excitability via 5-HT<sub>1B/1D</sub> receptor agonism in the brainstem and (3) vasoconstriction of meningeal, dural, cerebral or pial vessels as a result of vascular 5-HT<sub>1B</sub> receptor agonism.', 'Frovatriptan is a second generation triptan 5-HT receptor agonist that binds with high affinity for 5-HT<sub>1B</sub> and 5-HT<sub>1D</sub> receptors. It is structurally distinct from, but pharmacologically related to other selective 5-HT<sub>1B/1D</sub> receptor agonists. Frovatriptan has no significant effects on GABA<sub>A</sub> mediated channel activity and has no significant affinity for benzodiazepine binding sites. Frovatriptan is believed to act on extracerebral, intracranial arteries and to inhibit excessive dilation of these vessels in migraine. Research has shown that migraine can be caused by the swelling of blood vessels around the brain. Frovatriptan eases the pain associated with migraine by narrowing these blood vessels. Frovatriptan has one of the highest affinities for the 5-HT<sub>1B</sub> of the second-generation triptan agonists.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (26432, 'Sabotaz 0.1 % top. gel 15 gm', 'سابوتاز 0.1% جل 15جم', '20', NULL, 'Psoriasis', 'Tazarotene', 'Sabaa > sab for trading import & export', 'Gel', 'جل', '0.1 %', '1', NULL, 'indication used to treat psoriasis acne and sun damaged skin (photodamage). mechanism of action although the exact mechanism of tazarotene action is not known studies have shown that the active form of the drug (tazarotenic acid) binds to all three member', '6224000808716', NULL, 904, '2022-07-12', 0, 'Used to treat psoriasis, acne and sun damaged skin (photodamage).', 'Although the exact mechanism of tazarotene action is not known, studies have shown that the active form of the drug (tazarotenic acid) binds to all three members of the retinoic acid receptor (RAR) family: RARa, RARb, and RARg, but shows relative selectivity for RARb, and RARg and may modify gene expression. It also has affinity for RXR receptors.', 'Following topical application, tazarotene undergoes esterase hydrolysis to form its active metabolite, tazarotenic acid. When treating acne tazarotene may be taken in conjunction with an oral antibiotic. Tazarotene has been shown in peer-reviewed double blinded studies to reduce: mottling and hyperpigmentation, sallowness, fine wrinkling and coarse wrinkling in sun damaged skin. Histological studies have shown that long term (greater than 1 year) use of Tazarotene is associated with a significant reduction in atypical melanocytes and keratocytes - cells considered to be precursors of skin cancer. Some studies have shown long term use of Tazarotene to be associated with increased collagen production and better organization of skin collagen bundles.', 'DrugBank', 0, 0, 0);
