INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (23628, 'Nitrocare 2.5mg 30 s.r.caps.', 'نيتروكير 2.5مجم 30 كبسول', '14', NULL, 'Anti-ischemic', 'Nitroglycerin', 'T3a pharma', 'Cap', 'كبسولة', '2.5mg', '3', NULL, 'indication for the prevention of angina pharmacodynamics nitroglycerin an organic nitrate is available in many forms as a vasodilator. nitroglycerin is used in the treatement of angina pectoris and perioperative hypertension to produce controlled hypotens', NULL, NULL, 1253, '2022-07-16', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Nitroglycerin, also known as glyceryl trinitrate,[A180169] is an organic nitrate and a vasodilating agent [L38369] that was first discovered in 1847.[A249965] Originally used to dynamite, its antianginal effects were identified in the late 1860s after it produced headaches in factory workers while workers with angina pectoris or heart failure experienced relief from chest pain.[A180166,A180172] Its use as a treatment for angina dates back to 1879 and is still used to treat and prevent angina.[A249970] Nitroglycerin causes vasodilation in both arteries and veins.[A180172]

Nitroglycerin is used in a variety of different conditions, including angina pectoris due to coronary artery disease,[L7102,L7141,L38369,L42320] peri-operative hypertension, congestive heart failure,[L7099] and chronic anal fissure.[L42445] It is also used to induce intraoperative hypotension.[L7099]', '["C01DA02", "C01DA52", "C05AE01"]', '[]');
