INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (23957, 'Ocugent 3% eye/ear drops', 'اوكيوجنت 3% قطرة للعين / الأذن', '3', NULL, 'Aminoglycoside antibiotic', 'Gentamicin', 'Rameda', 'Drops', 'نقط', '3%', '1', NULL, 'about gentamicin aminoglycoside antibiotic. mechanism of action of gentamicin gentamicin exerts its bactericidal action against gram negative organisms & some of gram positive organisms by inhibiting bacterial protein synthesis. the process involves 1. pe', NULL, NULL, 745, '2022-07-09', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Gentamicin is a bactericidal aminoglycoside that was discovered and isolated from _Micromonospora purpurea_ in 1963.[A234349] It is one of the most frequently prescribed aminoglycosides due to its spectrum of activity, low cost, and availability.[A234339,A234354] Gentamicin is effective against both gram-positive and gram-negative organisms but is particularly useful for the treatment of severe gram-negative infections including those caused by _Pseudomonas aeruginosa_.[A233325,A234359,A234364] There is the added benefit of synergy when gentamicin is co-administered with other antibacterials such as beta-lactams.[A234364] This synergistic activity is not only important for the treatment of complex infections, but can also contribute to dose optimization and reduced adverse effects.[A234359,A234364]

Although gentamicin is well-established and may be used in a variety of clinical applications, it is also associated with severe adverse effects including nephrotoxicity and ototoxicity which may limit its use.[A234369]', '["S01AA11", "S02AA14", "D06AX07", "S03AA06", "J01GB03"]', '[]');
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (23958, 'Melatonin shahd pharma 3 mg 30 capsules', 'ميلاتونين شهد فارما 3مجم 30 كبسولة', '120', NULL, 'Sleep aid', 'Melatonin', 'Shahd pharma', 'Capsule', 'كبسولة', '3 mg', '3', NULL, NULL, '6224010912434', NULL, 923, '2024-08-31', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Melatonin is a biogenic amine that is found in animals, plants and microbes. Aaron B. Lerner of Yale University is credited for naming the hormone and for defining its chemical structure in 1958.  In mammals, melatonin is produced by the pineal gland. The pineal gland is small endocrine gland, about the size of a rice grain and shaped like a pine cone (hence the name), that is located in the center of the brain (rostro-dorsal to the superior colliculus) but outside the blood-brain barrier. The secretion of melatonin increases in darkness and decreases during exposure to light, thereby regulating the circadian rhythms of several biological functions, including the sleep-wake cycle. In particular, melatonin regulates the sleep-wake cycle by chemically causing drowsiness and lowering the body temperature.  Melatonin is also implicated in the regulation of mood, learning and memory, immune activity, dreaming, fertility and reproduction. Melatonin is also an effective antioxidant.  Most of the actions of melatonin are mediated through the binding and activation of melatonin receptors. Individuals with autism spectrum disorders (ASD) may have lower than normal levels of melatonin. A 2008 study found that unaffected parents of individuals with ASD also have lower melatonin levels, and that the deficits were associated with low activity of the ASMT gene, which encodes the last enzyme of melatonin synthesis. Reduced melatonin production has also been proposed as a likely factor in the significantly higher cancer rates in night workers.', '["N05CH01"]', '[]');
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (23959, 'Oculotob sterile ophthalmic 5ml susp.', 'اكيولوتوب قطرة معقمة للعين 5 ملي', '5', NULL, 'Aminoglycoside antibiotic', 'Tobramycin', 'Sigma tec > october pharma', 'Susp', 'معلق', '5ml', '1', NULL, NULL, NULL, NULL, 715, '2022-07-26', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Aminoglycosides, many of which are derived directly from _Streptomyces_ spp., are concentration-dependent bactericidal antibiotics with a broad spectrum of activity against Gram-positive and Gram-negative organisms.[A232294] Inhaled tobramycin is notable for its use in treating chronic _Pseudomonas aeruginosa_ infections in cystic fibrosis patients, as _P. aeruginosa_ is notoriously inherently resistant to many antibiotics.[A232294, A232299, L32739] However, tobramycin can also be administered intravenously and topically to treat a variety of infections caused by susceptible bacteria.[L32744, L32749] Its use is limited in some cases by characteristic toxicities such as nephrotoxicity and ototoxicity, yet it remains a valuable option in the face of growing resistance to front-line antibiotics such as β-lactams and cephalosporins.[A232294, A232349, L32739, L32749]

Tobramycin was approved by the FDA in 1975 and is currently available in a variety of forms for administration by inhalation, injection, and external application to the eye (ophthalmic).[L32739, L32744, L32749]', '["S01AA12", "J01GB01"]', '[]');
