INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (1772, 'Justcil 300 mg 20 tabs', 'جيستسيل 300 مجم 20 قرص', '90', NULL, 'Mucolytic', 'Acetylcysteine', 'Justin pharma', 'Tab', 'أقراص', '300 mg', '2', NULL, NULL, NULL, NULL, 2807, '2024-12-10', 'Acetylcysteine is indicated for mucolytic therapy and in the management of [acetaminophen] overdose.[L31378,L31383,L31388,L31478]', 'A number of possible mechanisms for the mucolytic activity of acetylcysteine have been proposed. Acetylcysteine''s sulfhydryl groups may hydrolize disulfide bonds within mucin, breaking down the oligomers, and making the mucin less viscous.[A228173,L31388] Acetylcysteine has also been shown to reduce mucin secretion in rat models.[A228173] It is an antioxidant in its own right but is also deacetylated to cysteine, which participates in the synthesis of the antioxidant glutathione.[A228173] The antioxidant activity may also alter intracellular redox reactions, decreasing phosphorylation of EGFR and MAPK, which decrease transcription of the gene MUC5AC which produces mucin.[A228173]



In the case of acetaminophen overdoses, a portion of the drug is metabolized by CYP2E1 to form the potentially toxic metabolite N-acetyl-p-benzoquinone imine (NAPQI).[L31388] The amount of NAPQI produced in an overdose saturates and depletes glutathione stores.[L31388] The free NAPQI promiscuously binds to proteins in hepatocytes, leading to cellular necrosis.[L31388] Acetylcysteine can directly conjugate NAPQI or provide cysteine for glutathione production and NAPQI conjugation.[L31388]', 'Acetylcysteine is indicated for mucolytic therapy and in the management of acetaminophen overdose.[L31378,L31383,L31388,L31478] It has a short duration of action as it is given every 1-8 hours depending on route of administration, and has a wide therapeutic window.[L31378,L31383,L31388,L31478] Patients should be counselled regarding diluting oral solutions in cola for taste masking,[A228153] the risk of hypersensitivity, and the risk of upper gastrointestinal hemorrhage.[L31378,L31383,L31388,L31478]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (4252, 'Epidexone 1mg/ml eye/ear drops 5 ml', 'ابيدكسون 1مجم/مل قطرة للعين 5 مل', '13', '7.5', 'Glucocorticoid', 'Dexamethasone', 'Eipico', 'Drops', 'نقط', '1mg', '1', NULL, 'about dexamethasone a potent synthetic glucocorticoid antiasthmatic anti-inflammatory and immunosuppressant. mechanism of action of dexamethasone the drug exerts it`s pharmacological action by penetrating and binding to cytoplasmic receptor protein and ca', '6221032335708', NULL, 1617, '2024-09-22', 'Dexamethasone and [ciprofloxacin] otic suspension is indicated for bacterial infections with inflammation in acute otitis media and acute otitis externa.[L10698] Intramuscular and intravenous injections are indicated for a number of endocrine, rheumatic, collagen, dermatologic, allergic, ophthalmic, gastrointestinal, respiratory, hematologic, neoplastic, edematous, and other conditions.[L10701] Oral tablets are indicated for the treatment of multiple myeloma.[L10710] An intravitreal implant is indicated for some forms of macular edema and non-infectious posterior uveitis affecting the posterior of the eye.[L10719] Various ophthalmic formulations are indicated for inflammatory conditions of the eye.[L10704,L10707,L10713,L10716,L10722,L10725]', 'The short term effects of corticosteroids are decreased vasodilation and permeability of capillaries, as well as decreased leukocyte migration to sites of inflammation.[A187463] Corticosteroids binding to the glucocorticoid receptor mediates changes in gene expression that lead to multiple downstream effects over hours to days.[A187463]



Glucocorticoids inhibit neutrophil apoptosis and demargination; they inhibit phospholipase A2, which decreases the formation of arachidonic acid derivatives; they inhibit NF-Kappa B and other inflammatory transcription factors; they promote anti-inflammatory genes like interleukin-10.[A187463]



Lower doses of corticosteroids provide an anti-inflammatory effect, while higher doses are immunosuppressive.[A187463] High doses of glucocorticoids for an extended period bind to the mineralocorticoid receptor, raising sodium levels and decreasing potassium levels.[A187463]', 'Corticosteroids bind to the glucocorticoid receptor, inhibiting pro-inflammatory signals, and promoting anti-inflammatory signals.[A187463] Dexamethasone''s duration of action varies depending on the route.[L10698,L10701,L10704,L10707,L10710,L10713,L10716,L10719,L10722,L10725] Corticosteroids have a wide therapeutic window as patients may require doses that are multiples of what the body naturally produces.[A187463] Patients taking corticosteroids should be counselled regarding the risk of hypothalamic-pituitary-adrenal axis suppression and increased susceptibility to infections.[A187463]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (15803, 'Captopril-h 50/25 mg 20 tabs.', 'كابتوبريل اتش 50/25 مجم 20 قرص', '16', NULL, 'Antihypertensive.combined ace with diuretic', 'Captopril+hydrochlorothiazide', 'Alexandria', 'Tab', 'أقراص', '25 mg', '2', NULL, 'about captopril a specific competitive angiotensin-converting enzyme (ace) inhibitor antihypertensive. about hydrochlorothiazide thiazide derivatibve diuretic. mechanism of action of hydrochlorothiazide it is thiazide diuretic which exerts its action by a', NULL, NULL, 3017, '2022-07-18', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (13854, 'Rangonal 20 film coated tablets', 'رانجونال 20 قرص', '80', '70', 'Folic acid.b complex', 'Folic acid + methylcobalamin + pyridoxine + thyamine', 'Vertvia pharma', 'Tablet', 'أقراص', NULL, '2', 'يستخدم لعلاج الانيميا وتحسين الجهاز العصبى', NULL, NULL, NULL, 2307, '2025-08-06', 'Folic acid is indicated for the treatment of folic acid deficiency, megaloblastic anemia, and in anemias of nutritional origins, pregnancy, infancy, or childhood.', 'Folic acid, as it is biochemically inactive, is converted to tetrahydrofolic acid and methyltetrahydrofolate by dihydrofolate reductase (DHFR). These folic acid congeners are transported across cells by receptor-mediated endocytosis where they are needed to maintain normal erythropoiesis, synthesize purine and thymidylate nucleic acids, interconvert amino acids, methylate tRNA, and generate and use formate. Using vitamin B12 as a cofactor, folic acid can normalize high homocysteine levels by remethylation of homocysteine to methionine via methionine synthetase.', 'Folic acid is a water-soluble B-complex vitamin found in foods such as liver, kidney, yeast, and leafy, green vegetables. Also known as folate or Vitamin B9, folic acid is an essential cofactor for enzymes involved in DNA and RNA synthesis. More specifically, folic acid is required by the body for the synthesis of purines, pyrimidines, and methionine before incorporation into DNA or protein. Folic acid is the precursor of tetrahydrofolic acid, which is involved as a cofactor for transformylation reactions in the biosynthesis of purines and thymidylates of nucleic acids. Impairment of thymidylate synthesis in patients with folic acid deficiency is thought to account for the defective deoxyribonucleic acid (DNA) synthesis that leads to megaloblast formation and megaloblastic and macrocytic anemias. Folic acid is particularly important during phases of rapid cell division, such as infancy, pregnancy, and erythropoiesis, and plays a protective factor in the development of cancer. As humans are unable to synthesize folic acid endogenously, diet and supplementation is necessary to prevent deficiencies. In order to function properly within the body, folic acid must first be reduced by the enzyme dihydrofolate reductase (DHFR) into the cofactors dihydrofolate (DHF) and tetrahydrofolate (THF). This important pathway, which is required for de novo synthesis of nucleic acids and amino acids, is disrupted by anti-metabolite therapies such as [DB00563] as they function as DHFR inhibitors to prevent DNA synthesis in rapidly dividing cells, and therefore prevent the formation of DHF and THF. 



In general, folate serum levels below 5 ng/mL indicate folate deficiency, and levels below 2 ng/mL usually result in megaloblastic anemia.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (19023, 'Tomazine 10mg 30 f.c. tabs.', 'تومازين 10مجم 30 قرص', '63', NULL, 'Anti-histamine.anti-allergy', 'Cetirizine', 'Alfacure pharmaceuticals', 'Tab', 'أقراص', '10mg', '3', NULL, NULL, '6224007711729', NULL, 1209, '2023-08-01', '**Seasonal Allergic Rhinitis**: Indicated for the relief of symptoms associated with seasonal allergic rhinitis caused by allergens such as ragweed, grass and tree pollens in adults and children 2 years of age and above. Symptoms treated effectively include sneezing, rhinorrhea, nasal pruritus, ocular pruritus, tearing, and redness of the eyes [FDA label]. 



**Perennial allergic rhinitis**: This drug is indicated for the relief of symptoms associated with perennial allergic rhinitis due to allergens including dust mites, animal dander, and molds in adults and children 6 months of age and older. Symptoms treated effectively include sneezing, rhinorrhea, postnasal discharge, nasal pruritus, ocular pruritus, and tearing [FDA label]. 



**Chronic urticaria**: Cetirizine is indicated for the treatment of the uncomplicated skin manifestations of chronic idiopathic urticaria in adults and children 6 months of age and older. It markedly reduces the occurrence, severity, and duration of hives and significantly reduces pruritus [FDA label].', 'Cetirizine, a metabolite of _hydroxyzine_, is an antihistamine drug.  Its main effects are achieved through selective inhibition of peripheral H1 receptors. The antihistamine activity of cetirizine has been shown in a variety of animal and human models. _In vivo_ and _ex vivo_ animal models have shown insignificant anticholinergic and antiserotonergic effects. In clinical studies, however, dry mouth was found to be more frequent with cetirizine than with a placebo. In vitro receptor binding studies have demonstrated no detectable affinity of cetirizine for histamine receptors  other than the H1 receptors. Studies with radiolabeled cetirizine administration in the rat have demonstrated insignificant penetration into the brain. _Ex vivo_ studies in the mouse have shown that systemically administered cetirizine does not occupy cerebral H1 receptors significantly [FDA label].', '**General effects and respiratory effects**



Cetirizine, the active metabolite of the piperazine H<sub>1</sub>-receptor antagonist hydroxyzine, minimizes or eliminates the symptoms of chronic idiopathic urticaria, perennial allergic rhinitis, seasonal allergic rhinitis, allergic asthma, physical urticaria, and atopic dermatitis.

The clinical efficacy of cetirizine for allergic respiratory diseases has been well established in numerous trials [FDA label]. 



**Effects on urticaria/anti-inflammatory effects**



It has anti-inflammatory properties that may play a role in asthma management [A175051]. There is evidence that cetirizine improves symptoms of urticaria. Marked clinical inhibition of a wheal and flare response occurs in infants, children as well as adults within 20 minutes of one oral dose and lasts for 24 h [A175051].  Concomitant use of cetirizine reduces the duration and dose of topical anti-inflammatory formulas used for the treatment of atopic dermatitis [A175051]. ', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (4906, 'Flagyl 500mg 20 tab.', 'فلاجيل 500مجم 20 قرص', '34', '15', 'Antiprotozoal.antidiarrheal', 'Metronidazole', 'Sanofi', 'Tab', 'أقراص', '500mg', '2', 'مطهر معوي', NULL, '6223003990602', NULL, 26005, '2023-09-26', 'Metronidazole is indicated for the treatment of confirmed trichomoniasis caused by Trichomonas vaginalis (except for in the first trimester of pregnancy) and the patient''s sexual partners, bacterial vaginosis,[L7432, L49811] certain types of amebiasis, and various anaerobic infections.[A181057, L49811] The above anaerobic infections may occur on the skin and skin structures, the abdomen, the heart, reproductive organs, central nervous system, and the respiratory system. Some may also be present in the bloodstream in cases of septicemia. Common infections treated by metronidazole are Bacteroides species infections, Clostridium infections, and Fusobacterium infections, as well as Peptococcus and Peptostreptococcus infections.[L3754] Topical formulations of metronidazole are indicated for the treatment of inflammatory lesions of rosacea.[L45166]



It is also used off-label in the treatment of Crohn''s disease, as a prophylactic agent after surgery[A181039], and in the treatment of Helicobacter pylori infection.[A181045] It has also been studied in the prevention of preterm births and to treat periodontal disease.[A1391,A181078]', 'The exact mechanism of action of metronidazole has not been fully established, however, it is possible that an intermediate in the reduction of metronidazole which is only made by anaerobic bacteria and protozoa, binds deoxyribonucleic acid and electron-transport proteins of organisms, blocking nucleic acid synthesis.[L3754]

After administration, metronidazole enters cells by passive diffusion. Following this, ferredoxin or flavodoxin reduce its nitro group to nitro radicals.

The redox potential of the electron transport portions of anaerobic or microaerophilic microorganisms renders metronidazole selective to these organisms, which cause nitro group reduction, leading to the production of toxic metabolites. These include N-(2-hydroxyethyl) oxamic acid and acetamide, which may damage DNA of replicating organisms.[A181039]', 'Metronidazole treats amebiasis, trichomoniasis, and giardiasis, exerting both antibacterial and antiprotozoal activities.[L7432] Metronidazole is an effective treatment for some anaerobic bacterial infections.[A181057] Metronidazole has shown antibacterial activity against the majority of obligate anaerobes, however, during in vitro studies, it does not demonstrate significant action against facultative anaerobes or obligate aerobes.[L3754] The nitro group reduction of metronidazole by anaerobic organisms is likely responsible for the drug''s antimicrobial cytotoxic effects, causing DNA strand damage to microbes.[A181039,A181045]



A note on convulsions and neuropathy and carcinogenesis



It is important to be aware of the risk of peripheral neuropathy and convulsions associated with metronidazole, especially at higher doses. If convulsions or numbness of an extremity occur, discontinue the drug immediately.[L3754] Metronidazole has been found to be carcinogenic in mice and rats. The relevance to this effect in humans is unknown. It is advisable to only administer metronidazole when clinically necessary and only for its approved indications.[L7474]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (7906, 'Melocam 15mg 5 supp.', 'ميلوكام 15مجم 5 اقماع لبوس', '9', NULL, 'Nsaid.oxicam', 'Meloxicam', 'Amoun', 'Supp', 'لبوس', '15mg', '1', NULL, 'about meloxicam nsaid thiazole derivative oxicam class analgesic anti-inflammatory. mechanism of action of meloxicam this drug reduces the prostaglandin synthesis peripherally by inhibiting cox1 & cox2 isoenzymes. this results its antinflammatory action.', '6221025009555', NULL, 1069, '2022-12-06', 'Meloxicam is indicated for the symptomatic treatment of arthritis and osteoarthritis.  In addition, it is indicated for the pauciarticular and polyarticular course of Juvenile Rheumatoid Arthritis (JRA) in patients aged 2 years old or above.[L11398] Off-label uses include the treatment of dental or post-surgical pain. In addition to the above, meloxicam has also been studied in the treatment of neuropathic pain. [A190189]



Meloxicam, in combination with [bupivacaine], is indicated for postsurgical analgesia in adult patients for up to 72 hours following soft tissue surgical procedures, foot and ankle procedures, and other orthopedic procedures in which direct exposure to articular cartilage is avoided.[L50427]', 'Meloxicam inhibits prostaglandin synthetase (cylooxygenase 1 and 2) enzymes leading to a decreased synthesis of prostaglandins, which normally mediate painful inflammatory symptoms.[A176366] As prostaglandins sensitize neuronal pain receptors, inhibition of their synthesis leads to analgesic and inflammatory effects. Meloxicam preferentially inhibits COX-2, but also exerts some activity against COX-1, causing gastrointestinal irritation.[A190189,L11398]', 'Meloxicam is an anti-inflammatory, analgesic analgesic with antipyretic effects in fever.[A190189] Prostaglandins are substances that contribute to inflammation.[A176366] This drug also exerts preferential actions against COX-2[A190195], which may reduce the possible gastrointestinal effects of this drug.  



In humans, meloxicam has demonstrated the ability to decrease erythrocyte sedimentation rate(ESR) in patients with rheumatoid arthritis, and to decrease ESR, C-reactive protein (CRP), as well as  aquaporin-1 expression.[A190189] As with other NSAIDS, prolonged use of meloxicum can result in renal or cardiovascular impairment or thrombotic cardiovascular events.[L11398]



A note on gastrointestinal effects



As meloxicam preferentially inhibits COX-2, it is thought to cause less gastrointestinal irritation compared to other NSAIDS. Despite this, it still carries a risk of gastric inflammation, bleeding and ulceration.[A190201,L11398]   In one study, patients on meloxicam suffered from gastrointestinal symptoms at a rate of 13% compared to 19% of those on [diclofenac]. GI events were found to be less severe in the meloxicam-treated patients.[A190189]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (24097, 'Omniscan 0.5mmol/ml (20ml) vial for i.v. inj.', 'اومنيسكان 0.5ممول/مل امبول 20مل', '340', NULL, 'Contrast agent', 'Gadodiamide', 'Ge healthcare-ireland > new technology', 'Inj', 'حقن', '20ml', '1', NULL, 'description gadodiamide is a gadolinium based contrast agent used in mr imaging procedures to assist in the visualization of blood vessels. mechanism of action based on the behavior of protons when placed in a strong magnetic field which is interpreted an', NULL, NULL, 1208, '2022-07-13', 'Gadodiamide is indicated for the visualization of lesions with abnormal vascularity in the brain (intracranial lesions), spine, and associated tissues and the body (including  the thoracic (noncardiac), abdominal, pelvic cavities, and retroperitoneal space) by the FDA and Health Canada.[L36873,L49881] Additionally, gadoliamide is approved by Health Canada to detect and localize tenosis in renal arteries and aorto-iliac arteries in magnetic resonance angiography (MRA).[L36873]

', 'Gadodiamide  paramagnetic molecule that develops a magnetic moment when placed in a magnetic field. The magnetic moment alters the relaxation rates of water protons in its vicinity in the body. Its use in magnetic resonance imaging (MRI) allows to selectively increase contrast in tissues where gadodiamide accumulates.[L49881]', 'In magnetic resonance imaging, visualization of normal and pathologic tissue depends in part on variations in the radiofrequency signal intensity. These variations occur due to: changes in proton density; alteration of the spin-lattice or longitudinal relaxation time (T1); and variation of the spin-spin or transverse relaxation time (T2). Gadodiamide is a paramagnetic agent with unpaired electron spins which generate a local magnetic field. As water protons move through this local magnetic field, the changes in the magnetic field experienced by the protons reorient them with the main magnetic field more quickly than in the absence of a paramagnetic agent.[L49881]



By increasing the relaxation rate, gadodiamide decreases both the T1 and T2 relaxation times in tissues where it is distributed. At clinical doses, the effect is primarily on the T1 relaxation time and produces an increase in signal intensity. Disruption of the blood-brain barrier or abnormal vascularity allows the accumulation of gadodiamide in lesions such as neoplasms, abscesses, and subacute infarcts. The pharmacokinetic parameters of gadodiamide in various lesions are not known.[L49881]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (28873, 'Ursogall 250mg/5ml 100ml susp.', 'اورسوجول 250مجم/5مل 100مل شراب معلق', '24', NULL, 'Liver support supplements', 'Ursodeoxycholic acid', 'Sigma', 'Susp', 'معلق', '250mg', '1', NULL, 'about ursodeoxycholic acid gallstone dissolution agent a bile acid prophylaxis and treatment of gall stones chronic hepatitis liver transplant rejection. mechanism of action of ursodeoxycholic acid ursodeoxycholic acid is a bile acid. normally there is a', '6221051110164', NULL, 1340, '2022-12-14', 'Ursodeoxycholic acid is indicated for the treatment of patients with primary biliary cholangitis.[L44627] 



It is used for the short-term treatment of radiolucent, noncalcified gallbladder stones in patients selected for elective cholecystectomy. It is also used to prevent gallstone formation in obese patients experiencing rapid weight loss.[L44793]', 'Endogenous hydrophobic bile acids such as deoxycholic acid and chenodeoxycholic acid can exert hepatotoxic effects. Ursodeoxycholic acid is a hydrophilic bile acid that mediates its biological effects via several mechanisms. Ursodeoxycholic acid (UDCA) protects hepatocytes and cholangiocytes from bile acid-induced damage, such as reactive oxygen species (ROS)-induced inflammation and mitochondrial dysfunction.[A256267] UDCA was shown to reserve hepatocyte cell structures and stimulate anti-apoptotic pathways.[A256267,A256272,A256458,A256463] It was also shown to prevent the production of ROS by Kupffer cells and resident macrophages in the liver, thus attenuating oxidative stress in the liver.[A256267] 



UDCA can also change the hydrophobicity index of the bile acid pool: following oral administration, UDCA forms a major fraction of the human bile acid pool and competitively displaces the hydrophobic or more toxic bile acids.[A256272,A256267] It increases the absorption of hydrophilic bile acids.[A256267] There are several proposed mechanisms of choleretic actions of UDCA: UDCA increases intracellular calcium levels, stimulating transport proteins and vesicular exocytosis in cholestatic hepatocytes.[A256267] UDCA may also upregulate the expression of membrane transport proteins like the chloride-bicarbonate anion exchanger (AE2),[A256267,A256458] which is involved in biliary secretion and is often observed to be defective in primary biliary cholangitis.[A256448] In rats, UDCA reduced hepatic expression of major histocompatibility complex (MHC) class I antigens, suggesting immunomodulating effects.[A256267] UDCA acts as a partial agonist at the bile acid receptor, also known as the farnesoid X receptor (FXR), and has negligible effects on cholesterol and lipid synthesis.[A256272]', 'Ursodeoxycholic acid (UDCA) is a secondary bile acid with cytoprotectant, immunomodulating, and choleretic effects. It reduces the cholesterol fraction of biliary lipids.[A256267]



UDCA inhibits the absorption of cholesterol in the intestine and the secretion of cholesterol into bile, decreasing biliary cholesterol saturation.[A256267] UDCA increases bile acid flow and promotes the secretion of bile acids.[A256267,A256277,A256463]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (8543, 'Naleve 375 mg 10 enteric coated tab', 'ناليف 375 مجم 10 اقراص', '5', NULL, 'Nsaid.propionic acid derivatives', 'Naproxen', 'Sigma', 'Tab', 'أقراص', '375 mg', '1', NULL, NULL, NULL, NULL, 781, '2022-08-23', 'Naproxen is indicated for the management of rheumatoid arthritis, osteoarthritis, ankylosing spondylitis, polyarticular juvenile idiopathic arthritis, tendinitis, bursitis, acute gout, primary dysmenorrhea, and for the relief of mild to moderate pain.[L6582][L6583][A178975] Further, it is first-line therapy for osteoarthritis, acute gouty arthritis, dysmenorrhea, and musculoskeletal inflammation and pain.[A178975]', 'As with other non-selective NSAIDs, naproxen exerts it''s clinical effects by blocking COX-1 and COX-2 enzymes leading to decreased prostaglandin synthesis.[A178975] Although both enzymes contribute to prostaglandin production, they have unique functional differences.[A178975] The COX-1 enzymes is constitutively active and can be found in normal tissues such as the stomach lining, while the COX-2 enzyme is inducible and produces prostaglandins that mediate pain, fever and inflammation.[A178990] The COX-2 enzyme mediates the desired antipyretic, analgesic and anti-inflammatory properties offered by Naproxen, while undesired adverse effects such as gastrointestinal upset and renal toxicities are linked to the COX-1 enzyme.[A178990]', 'Naproxen is an established non-selective NSAID and is useful as an analgesic, anti-inflammatory and antipyretic.[A179098] Similar to other NSAIDs, the pharmacological activity of naproxen can be attributed to the inhibition of cyclo-oxygenase, which in turn reduces prostaglandin synthesis in various tissues and fluids including the synovial fluid, gastric mucosa, and the blood.[A179098] 



Although naproxen is an effective analgesic, it can have unintended deleterious effects in the patient. For instance, naproxen can adversely affect blood pressure control.[A179224] A study found that use of naproxen induced an increase in blood pressure, although the increase was not as significant as that found with ibuprofen use.[A179224]



Further, studies have found that the risk of upper gastrointestinal bleeding is on average four-fold higher for individuals taking NSAIDs.[A179227] Other factors that increase the risk of upper gastrointestinal bleeding include concurrent use of corticosteroids or anticoagulants, and a history of gastrointestinal ulcers.[A179227]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (18574, 'Eso-protocol 20 mg 10 caps.', 'ايزو-بروتوكول 20 مجم 10 كبسولات', '42', NULL, 'Peptic ulcer.proton pump inhibitor', 'Esomeprazole', 'Medizen pharmaceutical industries > sanofi', 'Cap', 'كبسولة', '20 mg', '1', NULL, 'about esomeprazole a proton pump inhibitor substituted benzimidazole antiulcer antisecretory drug. mechanism of action of esomeprazole esomeprazole is the s enantiomer of omeprazole and is a proton pump inhibitor. it is a prodrug. after administration it', '6224000870386', NULL, 840, '2022-12-05', 'Esomeprazole is indicated for the treatment of acid-reflux disorders including healing and maintenance of erosive esophagitis, and symptomatic gastroesophageal reflux disease (GERD), peptic ulcer disease, H. pylori eradication, prevention of gastrointestinal bleeds with NSAID use, and for the long-term treatment of pathological hypersecretory conditions including Zollinger-Ellison Syndrome. ', 'Esomeprazole exerts its stomach acid-suppressing effects by preventing the final step in gastric acid production by covalently binding to sulfhydryl groups of cysteines found on the (H+, K+)-ATPase enzyme at the secretory surface of gastric parietal cells. This effect leads to inhibition of both basal and stimulated gastric acid secretion, irrespective of the stimulus. As the binding of esomeprazole to the (H+, K+)-ATPase enzyme is irreversible and new enzyme needs to be expressed in order to resume acid secretion, esomeprazole''s duration of antisecretory effect that persists longer than 24 hours.[FDA Label]

', 'Esomeprazole is a compound that inhibits gastric acid secretion and is indicated in the treatment of gastroesophageal reflux disease (GERD), the healing of erosive esophagitis, and <i>H. pylori</i> eradication to reduce the risk of duodenal ulcer recurrence. Esomeprazole belongs to a new class of antisecretory compounds, the substituted benzimidazoles, that do not exhibit anticholinergic or H2 histamine antagonistic properties, but that suppress gastric acid secretion by specific inhibition of the H<sup>+</sup>/K<sup>+</sup> ATPase at the secretory surface of the gastric parietal cell. By doing so, it inhibits acid secretion into the gsatric lumen. This effect is dose-related and leads to inhibition of both basal and stimulated acid secretion irrespective of the stimulus.



Esomeprazole is the s-isomer of [DB00338], which is a racemate of the S- and R-enantiomer. Esomeprazole has been shown to inhibit acid secretion to a similar extent as [DB00338], without any significant differences between the two compounds _in vitro_. 



PPIs such as esomeprazole have also been shown to inhibit the activity of dimethylarginine dimethylaminohydrolase (DDAH), an enzyme necessary for cardiovascular health. DDAH inhibition causes a consequent accumulation of the nitric oxide synthase inhibitor asymmetric dimethylarginie (ADMA), which is thought to cause the association of PPIs with increased risk of cardiovascular events in patients with unstable coronary syndromes.[A177577, A177580]



Due to their good safety profile and as several PPIs are available over the counter without a prescription, their current use in North America is widespread. Long term use of PPIs such as esomeprazole has been associated with possible adverse effects, however, including increased susceptibility to bacterial infections (including gastrointestinal _C. difficile_), reduced absorption of micronutrients including iron and B12, and an increased risk of developing hypomagnesemia and hypocalcemia which may contribute to osteoporosis and bone fractures later in life.[A177571]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (128, 'Acular ls 0.4% eye drops 5 ml', 'اكيولار ال اس 0.4% قطرة عين 5 مل', '42', NULL, 'Analgesic.nsaid.acetic acid derivatives', 'Ketorolac tromethamine', 'Allergan > soficopharm', 'Drops', 'نقط', '0.4%', '1', NULL, 'about ketorolac nsaid heterocyclic acetic acid derivative analgesic antipyretic antiinflammatory. mechanism of action of ketorolac this non steroidal anti inflammatory drug`s peripheral analgesics action is by the reduction of the prostaglandin synthesis', '5016007203790', NULL, 1839, '2022-12-01', 'Ketorolac is a Non-steroidal anti-inflammatory drug (NSAID) and has antipyretic, analgesic and anti-inflammatory properties.[A176131]  It is indicated for short term management of acute pain that requires the calibre of pain management offered by opioids.[L3674]  Clinicians may choose to initiate ketorolac to manage post-operative pain, spinal and soft tissue pain, rheumatoid arthritis, osteoarthritis, ankylosing spondylitis, menstrual disorders and headaches among other ailments.[L6520] Regardless of the etiology of pain, patients should use the lowest possible dose, and avoid using ketorolac for an extended period of time (ideally ≤ 5 days).[L3674] A benefit of choosing ketorolac over other analgesics with similar potency is that that there does not appear to be a risk of dependence or tolerance with ketorolac use.[A176131]     ', 'Ketorolac inhibits key pathways in prostaglandin synthesis which is crucial to it''s mechanism of action.[A176143]  Although ketorolac is non-selective and inhibits both COX-1 and COX-2 enzymes, it''s clinical efficacy is derived from it''s COX-2 inhibition. The COX-2 enzyme is inducible and is responsible for converting arachidonic acid to prostaglandins that mediate inflammation and pain.  By blocking this pathway, ketorolac achieves analgesia and reduces inflammation.[A176134] Ketorolac is administered as a racemic mixture; however, the "S" enantiomer is largely responsible for it''s pharmacological activity.[A176246]', 'Ketorolac is a non-selective NSAID and acts by inhibiting both COX-1 and COX-2 enzymes which are normally responsible for converting arachidonic acid to prostaglandins. The COX-1 enzyme is constitutively active and can be found in platelets, gastric mucosa, and vascular endothelium. On the other hand, the COX-2 enzyme is inducible and mediates inflammation, pain and fever.  



As a result, inhibition of the COX-1 enzyme is linked to an increased risk of bleeding and risk of gastric ulceration, while the desired anti-inflammatory and analgesic properties are linked to inhibition of the COX-2 enzyme.[A176134] Therefore, despite it''s effectiveness in pain management, ketorolac should not be used long-term since this increases the risk of serious adverse effects such as gastrointestinal bleeding, peptic ulcers, and perforations.[A178678]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (27810, 'Synerpen 375mg 12 tab.', 'سينربين 375مجم 12 اقراص', '32', NULL, 'Penicillins.penicillin with b-lactamase inhibitor', 'Ampicillin+sulbactam', 'T3a pharma', 'Tab', 'أقراص', '375mg', '1', NULL, 'other indications & uses beta-lactamase-producing: s. aureus e. coli klebsiella spp p. mirabilis bacteroides fragilis enterobacter spp acinetobacter calcoaceticus systemic adverse reactions systemic reactions reported in less than 1% of the patients were:', NULL, NULL, 869, '2022-07-20', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (2027, 'Captopril - alexandria 50mg 10 tabs.', 'كابتو بريل 50 مجم 10 اقراص', '14', '6.5', 'Antihypertensive.ace', 'Captopril', 'Alexandria', 'Tab', 'أقراص', '50mg', '1', 'علاج ارتفاع ضغط الدم', 'about captopril a specific competitive angiotensin-converting enzyme (ace) inhibitor antihypertensive. mechanism of action of captopril it is an angiotensin converting enzyme inhibitor prevents the conversion of angiotensin-1 to angiotensin-2 and abolishe', '6221042057614', NULL, 1067, '2025-07-03', 'For the treatment of essential or renovascular hypertension (usually administered with other drugs, particularly thiazide diuretics). May be used to treat congestive heart failure in combination with other drugs (e.g. cardiac glycosides, diuretics, &beta;-adrenergic blockers). May improve survival in patients with left ventricular dysfunction following myocardial infarction. May be used to treat nephropathy, including diabetic nephropathy. ', 'There are two isoforms of ACE: the somatic isoform, which exists as a glycoprotein comprised of a single polypeptide chain of 1277; and the testicular isoform, which has a lower molecular mass and is thought to play a role in sperm maturation and binding of sperm to the oviduct epithelium. Somatic ACE has two functionally active domains, N and C, which arise from tandem gene duplication. Although the two domains have high sequence similarity, they play distinct physiological roles. The C-domain is predominantly involved in blood pressure regulation while the N-domain plays a role in hematopoietic stem cell differentiation and proliferation. ACE inhibitors bind to and inhibit the activity of both domains, but have much greater affinity for and inhibitory activity against the C-domain. Captopril, one of the few ACE inhibitors that is not a prodrug, competes with ATI for binding to ACE and inhibits and enzymatic proteolysis of ATI to ATII. Decreasing ATII levels in the body decreases blood pressure by inhibiting the pressor effects of ATII as described in the Pharmacology section above. Captopril also causes an increase in plasma renin activity likely due to a loss of feedback inhibition mediated by ATII on the release of renin and/or stimulation of reflex mechanisms via baroreceptors. Captopril’s affinity for ACE is approximately 30,000 times greater than that of ATI.', 'Captopril, an ACE inhibitor, antagonizes the effect of the RAAS. The RAAS is a homeostatic mechanism for regulating hemodynamics, water and electrolyte balance. During sympathetic stimulation or when renal blood pressure or blood flow is reduced, renin is released from the granular cells of the juxtaglomerular apparatus in the kidneys. In the blood stream, renin cleaves circulating angiotensinogen to ATI, which is subsequently cleaved to ATII by ACE. ATII increases blood pressure using a number of mechanisms. First, it stimulates the secretion of aldosterone from the adrenal cortex. Aldosterone travels to the distal convoluted tubule (DCT) and collecting tubule of nephrons where it increases sodium and water reabsorption by increasing the number of sodium channels and sodium-potassium ATPases on cell membranes. Second, ATII stimulates the secretion of vasopressin (also known as antidiuretic hormone or ADH) from the posterior pituitary gland. ADH stimulates further water reabsorption from the kidneys via insertion of aquaporin-2 channels on the apical surface of cells of the DCT and collecting tubules. Third, ATII increases blood pressure through direct arterial vasoconstriction. Stimulation of the Type 1 ATII receptor on vascular smooth muscle cells leads to a cascade of events resulting in myocyte contraction and vasoconstriction. In addition to these major effects, ATII induces the thirst response via stimulation of hypothalamic neurons. ACE inhibitors inhibit the rapid conversion of ATI to ATII and antagonize RAAS-induced increases in blood pressure. ACE (also known as kininase II) is also involved in the enzymatic deactivation of bradykinin, a vasodilator. Inhibiting the deactivation of bradykinin increases bradykinin levels and may sustain its effects by causing increased vasodilation and decreased blood pressure.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (2980, 'Conida 4mg 30 tab.', 'كونيدا 4 مجم 30 اقراص', '24', NULL, 'Anti-diabetic.secretagogues.sulfonylurea', 'Glimepiride', 'Medizen pharmaceutical industries', 'Tab', 'أقراص', '4mg', '3', NULL, 'indications : this medication is a sulfonylurea antidiabetic agent prescribed for type 2 diabetes. it works by causing the pancreas to produce insulin dosage : the recommended starting dose is 1-2 mg once daily with food.maximum: 2mg/day contraindications', '6224000578015', NULL, 2373, '2022-12-04', 'Glimepiride is indicated for the management of type 2 diabetes in adults as an adjunct to diet and exercise to improve glycemic control as monotherapy. 



It may also be indicated for use in combination with metformin or insulin to lower blood glucose in patients with type 2 diabetes whose high blood sugar levels cannot be controlled by diet and exercise in conjunction with an oral hypoglycemic (a drug used to lower blood sugar levels) agent alone.[L10322]', 'ATP-sensitive potassium channels on pancreatic beta cells that are gated by intracellular ATP and ADP. The hetero-octomeric complex of the channel is composed of four pore-forming Kir6.2 subunits and four regulatory sulfonylurea receptor (SUR) subunits. Alternative splicing allows the formation of channels composed of varying subunit isoforms expressed at different concentrations in different tissues.[A177730] In pancreatic beta cells, ATP-sensitive potassium channels play a role as essential metabolic sensors and regulators that couple membrane excitability with glucose-stimulated insulin secretion (GSIS).[A177727] When there is a decrease in the ATP:ADP ratio, the channels are activated and open, leading to K+ efflux from the cell, membrane hyperpolarization, and suppression of insulin secretion.[A177727] In contrast, increased uptake of glucose into the cell leads to elevated intracellular ATP:ADP ratio, leading to the closure of channels and membrane depolarization. Depolarization leads to activation and opening of the voltage-dependent Ca2+ channels and consequently an influx of calcium ions into the cell.[A177727] Elevated intracellular calcium levels causes the contraction of the filaments of actomyosin responsible for the exocytosis of insulin granules stored in vesicles.[A177715] Glimepiride blocks the ATP-sensitive potassium channel by binding non-specifically to the B sites of both sulfonylurea receptor-1 (SUR1) and sulfonylurea receptor-2A (SUR2A) subunits as well as the A site of SUR1 subunit of the channel to promote insulin secretion from the beta cell.[A177715] ', 'Glimepiride stimulates the secretion of insulin granules from the pancreatic beta cells and improves the sensitivity of peripheral tissues to insulin to increase peripheral glucose uptake, thus reducing plasma blood glucose levels and glycated hemoglobin (HbA1C) levels. A multi-center, randomized, placebo-controlled clinical trial evaluated the efficacy of glimepiride (1–8 mg) as monotherapy titrated over 10 weeks compared with placebo in T2DM subjects who were not controlled by diet alone.[A177703] In this study, there was a reduction in fasting plasma glucose (FPG) by 46 mg/dL, post-prandial glucose (PPG) by 72 mg/dL, and HbA1c by 1.4% more than the placebo.[A177703] In another randomized study comprising of patients with T2DM receiving either placebo or one of the three doses (1, 4, or 8 mg) of glimepiride during a 14-week study period, all glimepiride regimens significantly reduced FPG, PPG, and HbA1c values (P < 0.001) compared to placebo by the end of the study period.[A177703] The 4- and 8-mg doses of glimepiride were more effective than the 1-mg dose; however, the 4-mg dose provided a nearly maximal antihyperglycemic effect.[A177703]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (14699, 'Avene eluage cream 30 ml', 'افين ايلواج كريم 30 مل', '429', NULL, 'Firming topical', NULL, 'Pierre - fabre france > biotech egypt', 'Cream', 'كريم', '30 ml', '1', NULL, 'corrective anti-aging cream with advanced technology to firm the skin and visibly reduce the appearance of deep lines and wrinkles. ï¿½ powerful ingredients work synergistically to diminish the appearance of deep lines and wrinkles without irritation ï¿½', NULL, NULL, 999, '2022-07-16', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (20938, 'Irban 300mg 20 tab.', 'ايربان 300 مجم 20 قرص', '36', NULL, 'Antihypertensive.angiotensin blocker', 'Irbesartan', 'Kahira', 'Tab', 'أقراص', '300mg', '2', NULL, 'about irbesartan angiotensin ii receptor blocker a non-peptide compound antihypertensive. mechanism of action of irbesartan it is a competitive antagonist of angiotensin-2 at at-1 receptor. it decreases peripheral resistance and lowers bp in hypertensive', NULL, NULL, 747, '2022-07-15', 'Irbesartan is indicated to treat hypertension and diabetic nephropathy in hypertensive patients with type 2 diabetes, elevated serum creatinine, and proteinuria.[L7456] A combination product with hydrochlorothiazide is indicated for hypertension in patients with uncontrolled hypertension with monotherapy or first line in patients not expected to be well controlled with monotherapy.[L7459]', 'Irbesartan prevents angiotensin II binding to the AT<sub>1</sub> receptor in tissues like vascular smooth muscle and the adrenal gland.[L7456,L7459] Irbesartan and its active metabolite bind the AT<sub>1</sub> receptor with 8500 times more affinity than they bind to the AT<sub>2</sub> receptor.[L7456,L7459] Irbesartan''s prevention of angiotensin II binding causes vascular smooth muscle relaxation and prevents the secretion of aldosterone, lowering blood pressure.[L7456,L7459]



Angiotensin II would otherwise bind to the AT<sub>1</sub> receptor, inducing vasoconstriction and aldosterone secretion, raising blood pressure.[L7456,L7459]', 'Irbesartan is an angiotensin receptor blocker used to treat hypertension and diabetic nephropathy.[L7456,L7459] It has a long duration of action as it is usually taken once daily and a wide therapeutic index as doses may be as low as 150mg daily but doses of 900mg/day were well tolerated in healthy human subjects.[L7456,L7459,A181165]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (20726, 'Imdur 60mg s.r 30 f.c. tab.', 'امديور 60مجم اس ار 30 قرص', '40', NULL, 'Antianginal', 'Isosorbide-5-mononitrate', 'Astra zeneca', 'Tab', 'أقراص', '60mg', '3', NULL, 'about isosorbide-5-mononitrate alkyl nitrate derivative a direct vasodilator antianginal. mechanism of action of isosorbide-5-mononitrate it is a direct smooth muscle relaxant which have a prominent action vascular smooth muscles preferentially veins than', '6223003270322', NULL, 1004, '2022-12-16', NULL, NULL, NULL, NULL, 0, 0, 0);
